content:
[[module ThemePreviewer noUi="true"]]

[[include :scp-wiki-cn:theme:basalt darkmode=a|hidetitle=a]]

[[module CSS]]
/*
=================
凤凰点阵体 - 12px
------------------------------
by Haoyu Qiu
授权： CC0 1.0
来源：https://timothyqiu.itch.io/vonwaon-bitmap
=================
*/
@import url(https://fastly.jsdelivr.net/gh/hoah2333/Fonts@main/Typeface-VonwaonBitmap-12px.css);
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@100;300;400;500;700');
@import url('https://scp-wiki.wikidot.com/local--files/scp-8980/departuremono.css');

/* CN适配 */
div#page-content div.page-rate-widget-box,
div#page-content :is(#u-credit-view, div#u-credit-otherwise) div.modalbox div[style*="text-align: center"] h2 span {
    display: none;
}

div#page-content div.rate-box-with-credit-button .creditButton {
    padding-inline-start: 0rem;
}

div#page-content :is(#u-credit-view, div#u-credit-otherwise) div.modalbox div[style*="text-align: center"] h2::before {
    content: "著作信息";
}

:root {
    --logo: url(http://scptestwiki.wikidot.com/local--files/sigma:lol/ecw.png);
    --header-title: "伦理委员会";
    --header-subtitle: "内部记录保存界面";
    --basalt-primary-color: 0, 15, 0;
    --basalt-secondary-color: 0, 22, 0;
    --basalt-tertiary-color: 0, 30, 0;
    --ec-popup-color: 10, 70, 10;
    --basalt-bright-element-color: 16, 184, 16;
    --basalt-dark-element-color: 10, 150, 10;
    --rfr-primary: #E0F6FD;
    --rfr-secondary: lightskyblue;
}

body {
    background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.8)), url("https://scp-wiki.wikidot.com/local--files/scp-8980/fractal.webp");
    background-position: center, center;
    background-size: cover;
    background-attachment: scroll, fixed;
}

b, strong {
    font-weight: 800;
}

table {
    box-sizing: border-box;
}

.content-warning {
    font-size: 85%;
    translate: 0px -5px;
}

#top-bar div[class*="top-bar"] > ul > li > a {
    font-size: 115%;
}

.page-title {
    border-bottom: solid 1px #424248;
    font-weight: normal;
    margin: 0 0 0.6em;
    padding: 0 0 0.25em;
    font-size: 200%;
    font-family: var(--title-font);
}

.page-title p {
    margin: 0;
}

#breadcrumbs, .pseudocrumbs {
    margin: auto;
    width: fit-content;
    padding-bottom: 5px;
    opacity: 0.8;
}

#page-content .yui-content div {
    overflow: hidden;
}

#page-content div:is(.document, .darkdocument) {
    max-width: 50rem;
}

div:is(.document, .darkdocument)::before, div:is(.document, .darkdocument)::after {
    position: relative !important;
}

div.blockquote {
    background-color: #0f0f0f;
}

div.blockquote.light {
    background-color: #2b2b2b;
}

.red-text {
    color: red;
}

.licensebox .collapsible-block-link {
    opacity: 0.5;
    transition-duration: 0.3s;
}

.licensebox .collapsible-block-link:hover {
    opacity: 1;
    transition-duration: 0.3s;
}

@media (max-width: 430px) {
    :where(#page-content) table {
        width: fit-content;
    }
}

[[/module]]

[[module CSS]]

.yui-nav li:first-child, .yui-nav li.selected {
    display: none;
    border: none;
}

.yui-navset.yui-navset-top {
    margin: 0;
    border: none;
    box-sizing: border-box;
}

.yui-navset.yui-navset-top div.yui-content {
    padding: 0px;
    border: none;
    box-sizing: border-box;
    overflow: auto;
}

.yui-nav li, .yui-nav li a, body .yui-navset.yui-navset-top > ul.yui-nav li a {
    border: none !important;
    border-top: none !important;
    gap: 0;
}

.yui-navset div.yui-content {
    background: transparent;
    border: none;
}

.yui-navset.yui-navset-top {
    display: grid;
    border: none;
}

.yui-navset .yui-nav {
    grid-row-start: 2;
    border: none;
}

#page-content {
    position: relative;
}

@keyframes fademodal {
  70% { visibility: hidden; }
  100% { visibility: visible; }
}

ul.modal-wrapper > li {
    list-style: none;
    position: absolute;
    top: -0.1rem;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 9;
    transition: opacity 0.3s ease-out;
    animation: 0.15s fademodal;
    animation-fill-mode: forwards;
}

ul.modal-wrapper > li.unfolded {
    visibility: visible;
    opacity: 1;
}

ul.modal-wrapper > li.folded {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    user-select: none;
}

.unfolded-ec-warning {
    backdrop-filter: blur(4px);
    -webkit-backdrop-filter: blur(4px);
}

ul:has(.unfolded) ~ div.yui-navset {
    filter: blur(4px);
}

.ec, .ec-popup {
    width: 100%;
    box-sizing: border-box;
    background-color: rgb(var(--basalt-secondary-color));
    border: 5px solid rgb(var(--ec-popup-color));
}

.ec {
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    padding: 10px;
    background-image: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/ETHICS_LIGHTLOGO.png);
    background-size: cover;
    background-position: center, center;
    background-blend-mode: darken;
    font-size: 90%;
}

.ec-popup {
    height: 70vh;
    overflow: auto;
    position: relative;
}

.ec-title {
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    font-size: 2rem;
    padding: 0;
    margin: auto;
}

.ec-grid {
    display: grid;
    width: 100%;
    height: 100%;
    grid-template-rows: min-content;
    grid-template-columns: repeat(3, minmax(0, 1fr))
}

.ec-popup-login, .ec-popup-date {
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    padding-left: 10px;
    font-size: 125%;
    opacity: 0.6;
    transition-duration: 0.3s;
}

:is(.ec-popup-login, .ec-popup-date):hover {
    opacity: 1;
    transition-duration: 0.3s;
}

.ec-popup-login {
    grid-column-start: 1;
    white-space: nowrap;
}

.ec-popup-login a {
    pointer-events: none;
}

.ec-popup-date {
    text-align: right;
    padding-right: 10px;
    grid-column-start: span 2;
}

.ec-popup-title {
    width: 100%;
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    font-size: 275%;
    text-align: center;
    box-sizing: border-box;
    margin: auto;
    margin-top: 0;
    padding: 20px;
    border-top: 8px double rgb(var(--ec-popup-color));
    border-bottom: 8px double rgb(var(--ec-popup-color));
    grid-column-start: span 3;
}

.ec-popup-desc {
    text-align: center;
    margin: auto;
    grid-column-start: span 3;
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    font-size: 1rem;
    width: 75%;
    box-sizing: border-box;
}

.ec-options {
    display: grid;
    width: 65%;
    grid-template-columns: repeat(3, minmax(0, 1fr));
    background: rgb(var(--basalt-secondary-color));
    margin: auto;
    aspect-ratio: 12/1 auto;
}

:is(.ec-theme-button, .ec-colorblind-button, .ec-accessibility-button) {
    text-align: center;
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    border: 5px solid rgb(var(--ec-popup-color));
    border-top: 0px;
    box-sizing: border-box;
    position: relative;
    transition-duration: 0.5s;
    display: grid;
}

.ec-button-inner-wrapper {
    margin: auto;
}

:is(.ec-theme-button, .ec-colorblind-button, .ec-accessibility-button) a {
    text-decoration: none !important;
}

.ec-button-inner-wrapper a::before {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    content: "";
}

.ec-theme-button {
    border-right: 2.5px solid rgb(var(--ec-popup-color));
}

.ec-colorblind-button {
    border-left: 2.5px solid rgb(var(--ec-popup-color));
    border-right: 2.5px solid rgb(var(--ec-popup-color));
}

.ec-accessibility-button {
    border-left: 2.5px solid rgb(var(--ec-popup-color));
}

:is(.ec-theme-button , .ec-accessibility-button, .ec-colorblind-button):hover {
    background-color: rgba(0, 0, 0, 0.5);
    transition-duration: 0.5s;
}

.ecinside {
    font-family: var(--body-font);
    font-size: var(--page-content-font-size);
    color: rgb(195, 195, 200);
}

.colorblind-mode-on, .light-mode-on {
    display: none;
}

:is(.ec, .ec-popup-login, .ec-popup-date, .ec-popup-title, .ec-popup-button) > p,
:is(.ec, .ec-popup-class, .ec-popup-status, .ec-popup-priority) div:not(.ecinside) > p {
    margin: auto;
}

:is(.ec-popup-title, .ec-popup-button) > p {
    line-height: 1.2;
}

.ec-popup-button {
    width: 40%;
    margin: auto;
    margin-bottom: 20px;
    padding: 10px;
    text-align: center;
    background-color: rgba(0, 0, 0, 0);
    transition-duration: 0.5s;
    grid-column-start: span 3;
    position: relative;
    border: 8px double rgb(var(--ec-popup-color));
    box-sizing: border-box;
    letter-spacing: 2px;
    cursor: pointer;
}

.ec-popup-button:hover {
    background-color: rgba(0, 0, 0, 0.5);
    transition-duration: 0.5s;
    letter-spacing: 10px;
}

.ec-popup-button :is(a, a:hover, a:active, a:focus) {
    text-decoration: none;
    pointer-events: none;
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    font-size: 370%;
}

.ec-popup-class, .ec-popup-status, .ec-popup-priority {
    display: flex;
    flex-direction: column;
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    text-align: center;
    border-bottom: 8px double rgb(var(--ec-popup-color));
}

:is(.ec-popup-class, .ec-popup-status, .ec-popup-priority) div:first-child {
    font-size: 120%;
    opacity: 0.7;
    margin-bottom: -10px;
}

:is(.ec-popup-class, .ec-popup-status, .ec-popup-priority) div:last-child {
    font-size: 175%;
}

.whitepop {
    color: rgb(195, 195, 200);
}

.ec-comments .blockquote {
    border: 3px double rgb(var(--ec-popup-color));
    background: rgba(var(--basalt-secondary-color), 0.7);
    width: 100%;
    margin: auto;
    margin-bottom: 20px;
}

.ec-comment {
    padding-left: 15px;
    margin-top: -7px;
}

@media (max-width: 733px) {
    .ec-popup {
        height: fit-content;
    }

    .ec-title {
        font-size: 7vw;
    }

    .ec-popup-title {
        font-size: 7vw;
    }

    .ec-popup-button:hover {
        letter-spacing: 5px;
    }

    .ec-popup-desc {
        font-family: 'Departure Mono', 'VonwaonBitmap 12px';
        font-size: 3vw;
        width: 100%;
        padding: 5px;
    }

    .ec-popup-login, .ec-popup-date {
        opacity: 1;
        font-size: 3.7vw;
        grid-column-start: span 3;
        text-align: center;
        padding-right: 10px;
        margin: auto;
    }

    div.ec-popup-button :is(a, a:hover, a:active, a:focus) {
        font-size: 4.5vw;
    }

    :is(.ec-popup-class, .ec-popup-status, .ec-popup-priority) div:last-child {
        font-size: 4.3vw;
    }

    .ec-options {
        width: 100%;
        font-size: 90%;
    }
}

@media (max-width: 450px) {
    div.scientistimage {
        width: fit-content;
        margin: auto;
        float: unset;
    }

    .ec-popup-button {
        width: 50vw;
        font-size: 1.5vw;
    }
}





:is(.bi-r, .bi-y, .bi-g, .bi-b) {
    padding: 0px 2px 0px;
}

.bi-r {
    background-color: rgba(255, 0, 0, 0.25);
}

.bi-y {
    background-color: rgba(255, 255, 0, 0.25);
}

.bi-g {
    background-color: rgba(0, 255, 0, 0.25);
}

.bi-b {
    background-color: rgba(0, 150, 255, 0.25);
}

:is(.bi-r, .bi-y, .bi-g, .bi-b) :is(a, a:hover, a:active, a:focus) {
    text-decoration: none;
    color: inherit;
}


:is(.bi-r, .bi-y, .bi-g, .bi-b) + .bi-popup {
    background-color: rgb(var(--basalt-secondary-color));
    background-image: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/ETHICS_LIGHTLOGO.png);
    background-size: cover;
    background-position: center, center;
    background-blend-mode: darken;
    border: 5px solid rgb(var(--ec-popup-color));
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    padding: 10px;
    font-size: 90%;
    opacity: 0;
    position: fixed;
    bottom: 4rem;
    left: 50%;
    translate: -50%;
    width: 65%;
    transition-duration: 0.2s;
    z-index: 8;
    visibility: hidden;
    max-height: calc(100vh - 12rem);
    overflow: scroll;
    font-style: normal;
}

:is(.bi-r, .bi-y, .bi-g, .bi-b) + .bi-popup::before {
    content: "审阅者注释";
    font-weight: bold;
    font-size: 135%;
    text-decoration: underline;
    display: block;
    text-align: center;
    color: #fff;
    margin-bottom: 5px;
}

:is(.bi-r, .bi-y, .bi-g) + .bi-popup strong {
    font-size: 115%;
    color: #fff;
    text-decoration: underline;
}

:is(.bi-r, .bi-y, .bi-g, .bi-b) + .bi-popup a {
    pointer-events: auto;
}

:is(.bi-r, .bi-y, .bi-g, .bi-b):is(:hover, :focus, :focus-within) + .bi-popup, .bi-popup:is(:hover, :focus, :focus-within) {
    opacity: 1;
    transition-duration: 0.2s;
    bottom: 5rem;
    visibility: visible;
}

:is(.bi-r, .bi-y, .bi-g, .bi-b) .scp-image-block {
    margin: .2rem .2rem .2rem .2rem;
    border-bottom: 0px;
}




.scientistimage {
    padding: 10px;
    float: right;
    position: relative;
    margin: 10px;
}

.rfr {
    display: grid;
    grid-template-columns: repeat(4, minmax(0, 1fr)) 3px 1fr;
}

.rfr-mobile {
    grid-template-columns: repeat(4, minmax(0, 1fr));
}

.rfr, .rfr-mobile {
    grid-template-rows: min-content;
    font-family: 'Times New Roman', 'Noto Serif SC';
    background: whitesmoke;
    color: #202020;
    width: 90%;
    margin: auto;
    border: 3px solid var(--rfr-primary);
}

.rfr-mobile {
    display: none;
}

.rfr > div, .rfr-mobile > div {
    position: relative;
}

@media (max-width: 570px) {
    .rfr {
        display: none;
    }

    .rfr-mobile {
        display: grid;
    }
}

.rfr-label {
    font-size: 80%;
    display: inline-block;
    width: 100%;
    background: var(--rfr-primary);
    padding-left: 1px;
}

.committee-reply {
    background-color: #b5906a;
    color: black;
    margin: auto;
    width: 81%;
    padding: 7px;
    border: 3px solid #523500;
}

.logtime {
    font-weight: 350;
    color: grey;
    font-size: 95%;
}

.email {
    background-color: rgba(var(--basalt-secondary-color), 0.9);
    border: 5px double rgb(var(--ec-popup-color));
    font-family: 'Departure Mono', 'VonwaonBitmap 12px';
    padding: 10px;
    margin: auto;
    width: 95%;
    font-size: 95%;
    box-sizing: border-box;
}

[[/module]]

