  }
}

.fragment-title {
  font-family: 'YES24', sans-serif;
  color: #901;
  font-size: 200%;
  font-weight: normal;
  margin: -30px 0 -20px 0;
}

::selection {
  background-color: #222;
  color: #FFF;
}

#page-content hr {
  margin: -0.8em 0 -0.7em 0;
  height: 1px;
  background: #0C0005;
}

#page-content .raisainfo hr {
  background-image: none;
  background: #AAA;
}

#side-bar hr, #page-content .modalcontainer .modalbox hr, #page-content blockquote hr {
  margin: 1em;
}

#breadcrumbs {
  display:none;
}

h2 {
  margin-bottom: 15px;
}

#page-title {
  margin-bottom: -10px;
}

.raisainfo h1, .raisainfo h2, .raisainfo h3 {
  font-family: "Trebuchet MS", Trebuchet, Verdana, "Nanum Gothic", Arial, Helvetica, sans-serif;
}

.raisainfo {
  font-family: verdana, "Nanum Gothic", arial, helvetica, sans-serif;
}

.header-kologo {
  display: flex;
  justify-content: center;
  background: url(http://scpko.wdfiles.com/local--files/thd-glasses-hub/scp_ko_bg.png) center no-repeat;
  background-size: contain;
  -webkit-background-size: contain;
  height: 500px;
}

.header-kologo h1 {
  font-size: 250%;
  color: #000;
}

@media (max-width: 767px) {
  .fragment-title {
    padding-top: 0.2em;
  }

  .creditRate {
    margin-right: 0 !important;
  }

  .header-kologo h1 {
    font-size: 200%;
  }

  .margin {
    display: none;
  }
}

@media (max-width: 450px) {
  .standard {
    display: none;
  }
}

@media (min-width: 449px) {
  .mobile {
    display: none;
  }
}

