[[module CSS]]

:root {

--admo-backdrop: url(https://scp-wiki.wikidot.com/local--files/scp-7243/5y46584875-1.webp);

--admo-color-bright: 117, 191, 151;
--admo-color-dim: 20, 20, 20;
--admo-color-dark: 0, 0, 0;
--admo-color-alt: 100, 140, 110;

--general-border-color: 60, 60, 60;
--basalt-tertiary-color: 51, 54, 54;
--darkdocument-background-color: 28, 28, 28;

--one-color: var(--admo-color-bright) !important;

--dougall: var(--admo-color-bright);
--ngo: var(--two-color);
--amelia: var(--four-color);
--ilse: var(--five-color);
--place: var(--six-color);

}

div.papernote {
background-color: rgb(202, 202, 202);
font-weight: 500;
color: rgb(var(--basalt-light-text-color));
}

.icon-2 {
filter: invert(0.75);
}

span.dd {
color: rgb(var(--dougall));
}

span.ngo {
color: rgb(var(--ngo));
}

span.at {
color: rgb(var(--amelia));
filter: grayscale(.33);
}

span.ir {
color: rgb(var(--ilse));
}

span.mcd {
color: rgb(var(--place));
}

.notation, .notation:is(.dd) {
border-color: rgba(var(--dougall), .66);
}

.notation:is(.at) {
border-color: rgba(var(--amelia), .66);
}

.notation:is(.ir) {
border-color: rgba(var(--ilse), .66);
}

.notation:is(.mcd) {
border-color: rgba(var(--place), .66);
}

@supports not (-webkit-touch-callout: none) {

@media only screen and (min-width: 1025px) {

:root {

--rate-module-background-color: var(--basalt-primary-color), 0.85;
--rate-module-text-color: 245, 245, 245;

}

#main-content {
margin-top: 0 !important;
}

#page-content {
padding-top: 0 !important;
}

#page-content > p:nth-of-type(1), #page-content > p:nth-of-type(2), #page-content > p:nth-of-type(3) {
margin: 0;
}

.admo-episode_splash {
position: relative;
left: calc(-50vw + 50%);
width: 100vw !important;
height: calc(125vh - var(--header-final-height));
max-width: 100vw !important;
background-image: url(https://scp-wiki.wikidot.com/local--files/scp-7243/fucksplosion-4.webp);
background-repeat: no-repeat;
background-size: cover;
background-position: center;
background-attachment: fixed;
margin-bottom: 15vh;
}

.admo-episode_splash:before {
content: "";
position: absolute;
left: calc(-50vw + 50%);
width: 100vw !important;
height: calc(150vh);
max-width: 100vw !important;
background-image: linear-gradient(180deg, transparent 0%, transparent 100vh, rgb(var(--basalt-primary-color)) 130vh, rgb(var(--basalt-primary-color)) 133vh, transparent 100%);
background-repeat: no-repeat;
background-size: cover;
background-position: center;
pointer-events: none;
}

.admo-episode_splash .admo-rate_splash {
margin-top: -26vh;
}

.admo-episode_splash, .admo-intermission_splash {
height: calc(110vh) !important;
}

.admo-episode_splash, .admo-accessibility a {
color: rgb(var(--basalt-light-text-color));
}

}

}

[[/module]]
