div#header {
    background: url(000) 10px 40px no-repeat;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "   ";
    color: #eee;

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "   ";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

