content:
[[div class="blackout" style="background: black; position:fixed;top:0;left:0;height:100vh;width:100vw;z-index:999"]]
[[/div]]

[[html]]
<head>
<style>
div{
    text-align: center;
}
.zerofive{
    text-align: right;
    color: white
}
span {
    opacity: 0
}
img {
    opacity: 0;
    width: 100vw
}
.show{
    opacity: 1
}
</style>
</head>
<body>
<div id="texts"></div>
<br />
<div>
<img src="http://scpsandboxcn.wdfiles.com/local--files/edwardc-include-parent/ru1.jpg" /> 
<img src="http://scpsandboxcn.wdfiles.com/local--files/edwardc-include-parent/ru2.jpg" /> 
<img src="http://scpsandboxcn.wdfiles.com/local--files/edwardc-include-parent/ru3.jpg" /> 
<img src="http://scpsandboxcn.wdfiles.com/local--files/edwardc-include-parent/ru4.jpg" /> 
</div>
<script>
text = "有人不想让你知道这些。有人让你忘记了他们的所作所为。ä有人让你忘记了他们为了所谓的“更大的善”都做了些什么。ä尽管如此，我们还是希望你能够ä记住我们。"
mapper = (c) => {
    if(c == "ä") return document.createElement("br");
    else {
        span = document.createElement("span"); 
        span.innerHTML = c;
        return span;
    }
}
spans = Array.from(text).map(mapper);
imgs = document.getElementsByTagName("img");
textsdiv = document.getElementById("texts");
for(span of spans){
    textsdiv.appendChild(span);
} 
function* generator(list){
    for(e of list) yield e;
}
typeWriter = (gen, callBack) => {
    state = gen.next();
    if (state.done) callBack();
    else state.value.className = "show";
}
var playimg = null;
var playtext = null;
afterimg = () => { 
    clearInterval(playimg);
}
aftertext = () => { 
    clearInterval(playtext); 
    playimg = setInterval(typeWriter, 1000,  generator(imgs), afterimg); 
}
setTimeout(() => { 
    playtext = setInterval(typeWriter, 150,  generator(spans), aftertext);
    }, 3500);
</script>
<div class="zerofive"><i>- 05-T，此处T指"Truth"</i></div>
</body>

[[/html]]

[[module css]]
/* CSS animation*/
.blackout{
    animation: fadeInAnimation step-end 3s;
    animation-iteration-count: 1;
    animation-fill-mode: forwards;
}

@keyframes fadeInAnimation {
    0% {
        opacity: 1;
    }
    100% {
        opacity: 0;
        height: 0vh;
     }
}

#page-title{
    display: none;
}

/* Classic SCP Foundation Theme
 * [2020 Wikidot Theme]
 * Created by Lt Flops
**/

/* ---- PAGE WRAP ---- */
 div#container-wrap{
     background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aclassic/classic_body_bg.png);
     background-repeat: repeat-x;
}

/* ---- HEADERS ---- */
 span, a{
     word-break: unset;
}
 h1{
     font-size: 190%;
}
 h1, h2, h3, h4, h5, h6{
     font-family: "Trebuchet MS", "Trebuchet", "Verdana", "Arial", "Helvetica", sans-serif;
}
 #header h1,
 #header h2{
     margin-left: -.16em;
}
 #header h1{
     max-height: 105px;
     margin: 0;
}
 #header h2{
     margin: 0;
}

/* ---- SITE HEADER ---- */
 #header h1 a,
 #header h2 span{
     color: transparent;
     text-shadow: none;
}
 #header h1 a{
     font-family: "Trebuchet MS", "Trebuchet", "Verdana", "Arial", "Helvetica", sans-serif;
     letter-spacing: 0;
}
 #header h1 a::before,
 #header h2 span::before{
     text-shadow: none;
}
 #header h1 a::before{
     content: "SCP基金会";
     color: hsl(0, 0%, 93%);
}
 #header h2 span::before{ 
     color: hsl(353, 100%, 30%);
}

/* ---- TOP MENU ---- */
 #header{
     background: none;
}
 #top-bar ul li,
 #top-bar ul li ul{
     box-shadow: none;
}
 #top-bar ul li ul{
     border-color: hsl(0, 0%, 27%);
     border-style: solid;
     border-width: 0 1px 1px;
}
 #top-bar ul li a,
 #top-bar ul li.sfhover a,
 #top-bar ul li:hover a{
     border-color: hsl(0, 0%, 27%);
     border-style: solid;
     border-width: 0 0 0 1px;
}
 #top-bar ul li a::before{
     content: "[";
}
 #top-bar ul li a::after{
     content: "]";
     position: relative;

/* NOTE: The Top Bar Menu has spaces after each bullet, which is why this is done: */
     left: -.3em;
}
 #top-bar ul li ul li a::before{
     content: unset;
}
 #top-bar ul li ul li a::after{
     content: unset;
}
 .mobile-top-bar{
     bottom: -.5px;
}

/* ---- SEARCH BOX ---- */
 #search-top-box input.empty{
     color: hsl(0, 0%, 45%);
}
 #search-top-box-input{
     color: hsl(0, 0%, 5%);
     background-color: hsl(0, 0%, 100%);
     border: 1px solid hsl(0, 0%, 47%);
     border-radius: 0;
     box-shadow: none;
}
 #search-top-box-input:hover,
 #search-top-box-input:focus{
     color: hsl(0, 0%, 5%);
     background-color: hsl(0, 0%, 100%);
     border: 1px solid hsl(0, 0%, 47%);
     box-shadow: none;
}
 #search-top-box-form input[type="submit"]{
     color: hsl(0, 0%, 5%);
     background: hsl(0, 0%, 96%);
     border: 1px solid hsl(0, 0%, 67%);
     border-radius: 0;
     box-shadow: none;
     font-size: 100%;
     font-weight: normal;
}
 #search-top-box-form input[type="submit"]:hover,
 #search-top-box-form input[type="submit"]:focus{
     color: hsl(0, 0%, 5%);
     background: hsl(0, 0%, 87%);
     border: 1px solid hsl(0, 0%, 67%);
     box-shadow: none;
}

/* ---- SIDE MENU ---- */
 #side-bar{
     clear: both;
     padding: .78em;
     padding-bottom: 0;
     border: 1px solid hsl(0, 0%, 87%);
     border-radius: 0;
     box-shadow: none;
}
 #side-bar .side-block{
     margin-bottom: .78em;
     background: transparent;
     border: none;
     border-radius: 0;
     box-shadow: none;
}
 #side-bar .heading{
     padding-left: 0;
     color: hsl(0, 0%, 5%);
     border-bottom: 1px solid hsl(0, 0%, 67%);
}
 #side-bar .collapsible-block-unfolded-link{
     border-bottom: 1px solid hsl(0, 0%, 67%);
}
 #side-bar .collapsible-block-unfolded-link .collapsible-block-link,
 #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover{
     margin-left: 0;
     color: hsl(0, 0%, 5%);
}

/* ---- INFO BAR ---- */
 body{
     --barColour: hsl(0, 0%, 28%);
}

/* ---- INFO PANE ---- */
.creditRate{
     margin-right: 0 !important;
     border: none !important;
}
 .rate-box-with-credit-button{
     background-color: hsl(0, 0%, 40%) !important;
     border: none !important;
     border-radius: 0 !important;
     box-shadow: none !important;
}
 .creditButton p a{
     color: hsl(0, 0%, 95%);
     border-left: none !important;
}
 .rate-box-with-credit-button .fa-info:hover{
     color: hsl(112, 100%, 77%);
}
 .modalbox{
     max-height: unset !important;
     border-radius: 0 !important;
     box-shadow: none !important;
}

/* ---- PAGE RATING ---- */
.page-rate-widget-box{
     margin-right: 0;
     border-radius: 0;
     box-shadow: none;
}
 .page-rate-widget-box .rate-points{
     background-color: hsl(0, 0%, 40%) !important;
     border: none;
     border-radius: 0;
}
 .page-rate-widget-box .rateup,
 .page-rate-widget-box .ratedown{
     background-color: hsl(0, 0%, 87%);
     border: none;
     font-weight: normal;
}
 .page-rate-widget-box .rateup a,
 .page-rate-widget-box .ratedown a{
     color: hsl(0, 0%, 5%);
}
 .page-rate-widget-box .rateup a:hover,
 .page-rate-widget-box .ratedown a:hover{
     color: hsl(0, 0%, 5%);
     background: hsl(0, 0%, 93%);
     text-decoration: underline;
}
 .page-rate-widget-box .cancel{
     background-color: hsl(0, 0%, 87%);
     border: none;
     border-radius: 0;
}
 .page-rate-widget-box .cancel a{
     color: hsl(0, 0%, 5%);
     text-transform: lowercase;
}
 .page-rate-widget-box .cancel a:hover{
     color: hsl(0, 0%, 5%);
     background: hsl(0, 0%, 93%);
     text-decoration: underline;
}

/* ----- GENERAL ----- */
 body{
     color: hsl(0, 0%, 5%);
     word-break: unset;
}
 hr{
     height: 0;
     margin: 1em 0;
     background-color: transparent;
     border-top: 1px solid hsl(0, 0%, 67%);
}

/* ---- IMAGE BLOCK ---- */
 .scp-image-block.block-right{
     margin: 0 0 1em 2em;
     box-shadow: none;
}

/* ---- CLASSIC IMAGE BLOCK DIV ---- */
 .classic-image-block{
     float: right;
     width: 300px;
     margin: 0 0 1em 2em;
     text-align: center;
}

/* ---- YUI TABS ---- */
 .yui-navset .yui-nav a,
 .yui-navset .yui-navset-top .yui-nav a{
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: hsl(0, 0%, 100%);
    /* -------- */
     background-image: none;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
    /* ---- Tab Background Colour | [HOVER] ---- */
     background-color: hsl(0, 49%, 91%);
    /* -------- */
     background-image: none;
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:hover{
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background-color: hsl(0, 70%, 35%);
    /* -------- */
     background-image: none;
}

/* --- INTERWIKI --- */
 iframe.scpnet-interwiki-frame{
     position: relative;
     width: 17em;
     height: 12em;
     margin-left: .313em;
     background-color: transparent;
}

/* ---- MOBILE MEDIA QUERIES ---- */
 @media (max-width: 479px){
      #top-bar ul li a{
          padding-left: 0.5em;
          padding-right: 0.5em;
     }
}
 @media (max-width: 767px){
       #side-bar .side-block{
          background-color: hsl(0, 0%, 100%);
     }
      #side-bar:after{
          background-color: hsla(0, 0%, 30%, .2);
     }
}
 @media (max-width: 479px),
 @media (min-width: 480px) and (max-width: 580px),
 @media (min-width: 581px) and (max-width: 767px){
      #side-bar .side-block{
          background-color: hsl(0, 0%, 100%);
     }
      #side-bar:after{
          background-color: hsla(0, 0%, 30%, .2);
     }
      #header, .mobile-top-bar{
          max-width: 95%;
     }
      .mobile-top-bar{
          left: 0;
     }
}
[[/module]]
