div#page-options-bottom>a.btn-default,div#page-info,div.mobile-top-bar,div.open-menu {
font-size:0;
display:none;
width:0;
height:0;
}


div#login-status>span.printuser {
font-size:0em;
display:none;
}
div#login-status>a#account-topbutton {
font-size:0em;
display:none;
height:0;
width:0;
}


#page-info {
color:white;
}

#page-content {

}

#page-title {
    display: block;
}

#page-options-container {
    display: block;
}

body {
background:url(https://img1.imgtp.com/2022/08/10/0XSzI4Q1.svg);
background-repeat:no-repeat;
background-attachment:fixed;
background-position:center center;
background-size:900px;
background-color:#19002d;

}

div#container-wrap {
    background: url(https://img1.imgtp.com/2022/08/10/fkDCv29F.svg) top left repeat-x !important;
}

p,
h1,
h2,
h3,
h4,
h5 {
color:rgb(255,255,255);
}

div#page-content a {
color:rgb(138,43,226);
}

div#page-content span.number,span.lang-cn,div.level {
color:rgb(255,255,255);
}

div#footer,div#license-area,div#options {
display:none;
}

div#footer {
background-color:rgb(0,0,0)
}

div#login-status {
color:red;
}

a#my-account {
display:none;
}

.printuser {
color:red;
font-style:italic;
border:solid 1px red;
}

.printuser:after {
color:red;
content:"不匹配任何一个现有的用户名";
font-style:normal;
}

#account-options {
display:none;
}

#header span {
color:red;
}

.small {
display:none;
}

div#main-content a {animation-fill-mode:forwards;}

div#main-content a:hover {
animation:aahover 0.4s;
color:rgb(0,0,0);
text-decoration:none;
animation-fill-mode:forwards;
}

@keyframes aahover {
from {background:rgba(0,0,0,0)}
to {background-color:rgb(200,180,255);}
}

/*User Not Found*/
.user {
border:solid 1px red;
color:red;
padding:1px;
}

/* content */



div.danger-diamond a {
display:none;
}



div.menu-item div.heading p {
color:black;
}

div#side-bar {
background-color:rgba(0,0,0,0.9);
}

#page-content strong,b {
color:#7B68EE
}

#page-content p {
font-size:1.15em;
}

#page-source * {
color:white;
}

