.btn1 {
background-color:#D8BFD8;
border-radius:10px;
width:25%;
height:auto;
margin:0 auto;
border:solid 2px #FFFFFF;
color:#FFFFFF;
box-shadow: 0px 0px 10px rgba(148,0,211,0.6);
}

.btn1:hover {
animation:btn1hover 0.4s;
color:rgb(0,0,0);
text-decoration:none;
animation-fill-mode:forwards;
}

@keyframes btn1hover {
from {background:#8A2BE2;box-shadow: 0px 0px 10px rgba(148,0,211,0.6);color:rgb(255,255,255);}
to {background-color:rgb(75,0,130);box-shadow: 0px 0px 15px rgba(148,0,211,1);color:rgb(0,0,0);}
