/*Anim 1 Box appears */

@keyframes appear0 {
0% {width:0;height:0;padding:0;border:solid 0px #000000;}
/* 40% {width:1px;height:auto;padding:0;border:solid 0px #000000;} */
100% {width:90%;height:auto;padding:5px 10px 5px 10px;border:solid 2px #7B68EE;}
