background:rgba(0,0,0,0.7);
width:0;
padding:0;
border:solid 0px #000000;
box-shadow: 5px 5px 5px rgba(148,0,211,0.2);
height:0;
margin:0 auto;
animation: appear0 0.4s forwards;
-webkit-animation: appear0 0.4s forwards;
overflow: hidden;
}

.say2 {
background:rgba(0,0,0,0.7);
width:0;
padding:0;
border:solid 0px #000000;
box-shadow: 5px 5px 5px rgba(148,0,211,0.2);
height:0;
margin:0 auto;
animation: blink 1s forwards;
-webkit-animation: blink 1s forwards;
overflow: hidden;
}

-webkit-@keyframes blink {
0% {width:0;height:0;padding:0;border:solid 0px #000000;}
1% {width:90%;height:auto;padding:5px 10px 5px 10px;border:solid 2px #7B68EE;}
99% {width:90%;height:auto;padding:5px 10px 5px 10px;border:solid 2px #7B68EE;}
100% {width:0;height:0;padding:0;border:solid 0px #000000;}
}

@keyframes blink {
0% {width:0;height:0;padding:0;border:solid 0px #000000;}
1% {width:90%;height:auto;padding:5px 10px 5px 10px;border:solid 2px #7B68EE;}
99% {width:90%;height:auto;padding:5px 10px 5px 10px;border:solid 2px #7B68EE;}
100% {width:0;height:0;padding:0;border:solid 0px #000000;}
}

-webkit-@keyframes fall {
0% {background:#000000}
1% {background:#FFFFFF}
100% {background:#000000}
}

.rotaimg {
background:url(https://img1.imgtp.com/2022/08/11/WawXV6Qp.svg);
background-size:50%;
margin:0 auto;
background-position:center center;
background-repeat:no-repeat;
animation:rota 1s infinite;
-webkit-animation:rota 1s infinite;
width:50vw;
height:50vw;
}

@keyframes rota {
from {transform:rotate(0deg)}
to {transform:rotate(360deg)}
}
-webkit-@keyframes rota {
from {transform:rotate(0deg)}
to {transform:rotate(360deg)}
}


.say3 {
