[[module CSS]]

#u-blink-bar p:last-child::after {
    content: "";
    border-right: 1px solid white;
    animation: blink 1.5s infinite;
    position: relative;
    left: 1px;
}

@keyframes blink{
    to {
        opacity: .0;
    }
}
[[/module]]
