[[include :scp-wiki:component:toggle-sidebar]]

[[module css]]

#page-title, #top-bar,  #header {
    display: none;
}

#content-wrap{
    margin: 0 auto;
    width: 100%;
    max-width: 100%;
}

div#container-wrap {
    background-image: url();
    background-color: #161616;
    margin: 0 0 0 0;
}

#action-area-top{
    display: none;
}

#main-content{
    margin: 0 0 0 0;
    width: 100%;
    max-width: 100%;
}

p{
    margin-block-start: 0em;
}

.html-block-iframe {
    max-height: 100vh;
    height: 100vh;
}

/* Rate Module */
.page-rate-widget-box {
  position: fixed;
  top: 1em;
  right: 1em;
  box-shadow: none;
  background-color: transparent !important;
}
.page-rate-widget-box .rate-points {
  padding: 3px;
  background-color: transparent !important;
  border: 2px solid #9c67b5;
  border-right: none;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel {
  padding: 3px;
  background-color: transparent;
  border: none;
  border-bottom: 2px solid #9c67b5;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
  background-color: transparent;
  color: #fff;
  text-decoration: none;
}
.rateup a:hover,
.ratedown a:hover,
.cancel a:hover {
  color: #fff;
  text-decoration: none;
  background-color: #9c67b5 !important;
}

#main-content a {
  color: #9c67b5;
}

.my-open-menu {
  position: absolute;
  top: 0.5em;
  left: 1em;
  width: 1.5em;
  height: 1.5em;
  z-index: 5;
  border: 3px #9c67b5 solid;
  border-radius: 3em;
  text-align: center;
  font-size: 20px;
}

[[/module]]


