[[include :scp-wiki-cn:theme:basalt]]
[[module css]]
#page-content div, #page-content div table {
    max-width: 100%;
}
.content-panel.series {
    padding: 0 20px;
    margin-bottom: 20px;
}
.content-panel.standalone {
    background: #fcfdfb;
}
.content-panel {
    border: solid 1px #888880;
    border-radius: 10px;
    background-color: #999990;
    margin: 10px 0 15px;
    box-shadow: 3px 3px 6px #bbb;
    box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
}
.hide1 {
    display: block;
    max-width: max-content;
    margin: auto;
    background: linear-gradient(180deg, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 50%, rgba(0,0,0,0) 50%, rgba(0,0,0,0) 100%);
    background-size: 100% 200%;
    -webkit-animation: show1 2.5s cubic-bezier(.4,0,.2,1);
    animation: show1 2.5s cubic-bezier(.4,0,.2,1);
    animation-fill-mode: forwards;
}
.hide2 {
    display: block;
    max-width: max-content;
    margin: auto;
    background:linear-gradient(180deg, rgba(0,0,0,0) 0%, rgba(0,0,0,0) 50%, rgba(0,0,0,1) 50%, rgba(0,0,0,1) 100%);
    background-size: 100% 200%;
    -webkit-animation: show2 2.5s cubic-bezier(.4,0,.2,1);
    animation: show2 2.5s cubic-bezier(.4,0,.2,1);
    animation-fill-mode: forwards;
}
.hide3 {
    display: block;
    max-width: max-content;
    margin: auto;
    background: linear-gradient(90deg, rgba(255,255,255,0) 0%, rgba(255,255,255,0) 50%, rgba(0,0,0,1) 50%, rgba(0,0,0,1) 100%);
    background-size: 200% 100%;
    -webkit-animation: show3 4s cubic-bezier(.4,0,.2,1);
    animation: show3 4s cubic-bezier(.4,0,.2,1);
    animation-fill-mode: forwards;
}
.scrivere {
    font-family: monospace !important;
    white-space: nowrap;
    overflow: hidden;
    animation: machina-de steps(11, end) 3s forwards;
    font-size: 4ch;
    font-weight: 900;
    -webkit-text-stroke: 3px;
}
@keyframes machina-de {
    0% {width: 0;}
    66.7% {width: 0;}
    100%{width: 11ch;}
}
@keyframes show1 {
    0% {background-position: 0 0;}
    20% {background-position:  0 0;}
    100% {background-position: 0 100%;}
}
@keyframes show2 {
    0% {background-position: 0 100%;}
    20% {background-position:  0 100%;}
    100% {background-position: 0 0;}
}
@keyframes show3 {
    0% {background-position: 100% 0;}
    50% {background-position:  100% 0;}
    100% {background-position: 0 0;}
}
@keyframes show4 {
    0% {border-right: 0 solid}
    50% {border-right: 0 solid}
    100% {border-right: .175rem solid}
}
.overseer {
    border: 2px solid #000;
    padding: 1em;
    border-radius: 2px;

    display: grid;
    grid-auto-columns: 1fr;
    grid-auto-rows: 1fr;
    grid-template-columns: 0.4fr 0.4fr 2.2fr;
    grid-template-rows: 1fr 1fr 1fr 1fr 1fr;
    gap: 1px 1px;
}
.cinfo, .signa {
    text-orientation: mixed;
    writing-mode: vertical-rl;
    display: flex;
    color: #000;
}
.cinfo { grid-area: 1 / 1 / 6 / 2; }
.signa {
    grid-area: 1 / 2 / 6 / 3;

    -webkit-animation: show4 1s cubic-bezier(.4,0,.2,1);
    animation: show4 1s linear;
    animation-fill-mode: forwards;
}
.alertxt {
    grid-area: 3 / 3 / 6 / 4;
    display: flex;
    align-items: center;
    padding: .5rem;

    background: transparent;
    -webkit-animation: scanline 4s cubic-bezier(.4,0,.15,1);
    animation: scanline 4s cubic-bezier(.4,0,.15,1);
    animation-fill-mode: forwards;
}
@keyframes scanline {
  0% {
        -webkit-clip-path: polygon(0 0, 0 0, 0 100%, 0% 100%);
        clip-path: polygon(0 0, 0 0, 0 100%, 0% 100%);
}
  49% {
        -webkit-clip-path: polygon(0 0, 0 0, 0 100%, 0% 100%);
        clip-path: polygon(0 0, 0 0, 0 100%, 0% 100%);
}
  100% {
        -webkit-clip-path: polygon(100% 0, 0 0, 0 100%, 100% 100%);
         clip-path: polygon(100% 0, 0 0, 0 100%, 100% 100%);
}
}
@keyframes scanpage {
  0% {
        -webkit-clip-path: polygon(-5% 0, 105% 0%, 105% 0, -5% 0);
        clip-path: polygon(-5% 0, 105% 0%, 105% 0, -5% 0);
}
  65% {
        -webkit-clip-path: polygon(-5% 0, 105% 0%, 105% 0, -5% 0);
        clip-path: polygon(-5% 0, 105% 0%, 105% 0, -5% 0);
}
  100% {
        -webkit-clip-path: polygon(-5% 0, 105% 0%, 105% 105%, -5% 105%);
        clip-path: polygon(-5% 0, 105% 0%, 105% 105%, -5% 105%);
}
}
.pagehold {
     display: block;
     width: fit-content;
     height: fit-content;

    background: transparent;
    -webkit-animation: scanpage 3.4s cubic-bezier(.4,0,.15,1);
    animation: scanpage 3.4s cubic-bezier(.4,0,.15,1);
    animation-fill-mode: forwards;
}
.stampost {
  grid-area: 1 / 3 / 6 / 4;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: 1fr 1fr 1fr;
  gap: 0px 0px;
  grid-template-areas:
    "scpmodal scpmodal scpmodal"
    "scpmodal scpmodal scpmodal"
    "scpmodal scpmodal scpmodal";
  grid-area: 1 / 3 / 4 / 4;
}
.scpquery {
    grid-area: 2 / 3 / 4 / 4;
    color: rgb(var(--dark-accent));
    display: inline-flex;
    justify-content: center;
    align-items: center;
}
.cinfo {
    align-items: center;
    justify-content: flex-start;
}
.signa {
    align-items: center;
    justify-content: flex-end;
}
.scpmodal {
    grid-area: scpmodal;
    background: url(https://ark-logistics.wdfiles.com/local--files/file%3Alimn/v3.png) center no-repeat;
    background-size: contain;
    z-index: -1;
    filter: drop-shadow(0px 0px 1px rgba(0,0,0,.4));

    -webkit-animation: load 4s cubic-bezier(.4,0,.2,1);
    animation: load 4s cubic-bezier(.4,0,.2,1);
    animation-fill-mode: forwards;
}
@keyframes load {
    0% {
       transform: scale(1.5) translateY(25%) rotateY(0);
}
    60% {
       transform: scale(1.5) translateY(25%) rotateY(360deg);
}
   100% {
       transform: scale(1) translateY(0) rotateY(360deg);
}
}
[[/module]]
