[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Noto+Serif+SC|Noto+Serif+TC&display=swap');
@import url('https://fonts.font.im/css?family=Special Elite');
ul.kanji {
    list-style: cjk-ideographic;
}
/* Table */
#page-content div.b-tables table.wiki-content-table, #page-content table.wiki-content-table {
    width: 100%;
}
#page-content div.b-tables table.wiki-content-table td, #page-content div.b-tables table.wiki-content-table th, #page-content table.wiki-content-table td, #page-content table.wiki-content-table th {
    width: 10em;
}
/* Overall Root*/
:root {
    --dot: "\25C8";
    --lock-mask: url("data:image/svg+xml;base64,");
}
/* 这是绿色表格 */
div.terminal{
    border: 1px solid black;
    border: solid 3px #BBBBBB;
    border-radius: 16px;
    background-color: #131;
    background-image:
        radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #121);
    background-position: center;
    display: block;
    box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
    overflow:hidden;
}
div.terminal blockquote {
    background-color: black;
    border: double 3px #80FF80
}
div.scanline{
    margin-top: -40%;
    width: 100%;
    height: 60px;
    position: relative;
    pointer-events: none;
/* Safari 4.0 - 8.0 */
    -webkit-animation: scan 12s linear 0s infinite; 
    animation: scan 12s linear 0s infinite;
    background: linear-gradient(to bottom, rgba(56, 112, 82,0), rgba(56, 112, 82,0.1)) !important;
}

div.text{
    color: rgba(128,255,128,0.8);
    padding-left: 2em;
    padding-top: 40%;
    font-family: monospace;
    font-size: 1.2em;
    }

/* Safari 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} 
}

@keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* 同上。 */
}

div.text a {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a.newpage {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a:hover {
    color: #131;
    text-decoration: underline;
    background-color: #80FF80;
    padding: 1px;ar(--dark-accent));
    padding: .5em;
    background-color: #ffffffa3;
}
.item-alias:before,.prototype:before,.timestamp:before,.councilor:before,.security:before {
    background-color: rgb(var(--dark-accent));
    padding: 0 2px 2px 0;
    display: inline-block;
    margin: calc(-.5em - 1px);
    color: #fff;
    font-size: .8rem;
    font-family: 'Noto Serif SC',serif;
    box-shadow: 0 0 3px #6c6c6c;
}
.contxt:before {
    background-color: rgb(var(--dark-accent));
    padding: 2px 2px 2px 2px;
    display: block;
    color: #fff;
    font-family: 'Noto Serif SC',serif;
    font-weight: 900;
    box-shadow: 0 0 3px #6c6c6c;
    width: max-content;
    margin: -.5em auto;
}
.item-alias:after,.prototype:after,.timestamp:after,.councilor:after,.security:after {
    font-size: .8rem;
    display: block;
    float: right;
    margin: -1em 0px 0 0;
    transform: translateX(-5px);
    font-family: 'Special Elite', cursive;
    color: #000000;
    -webkit-text-stroke: 0px #00000038;
    filter: drop-shadow(0px 1px 1px #fff);
    mix-blend-mode: difference;
    text-transform: Uppercase;
}
.item-alias:before {
    content: "项目代号";
}
.prototype:before {
    content: "项目原型";
}
.timestamp:before {
    content: "时间";
}
.councilor:before {
    content: "领导人员";
}
.security:before {
    content: "保密等级";
}
.contxt:before {
    content: "提案正文";
}
.item-alias:after {
    content: "recognon";
}
.prototype:after {
    content: "prototype";
}
.timestamp:after {
    content: "timestamp";
}
.councilor:after {
    content: "councils";
}
.security:after {
    content: "level";
}
.padlock {
    color: black;
    border: solid 1px #000;
    background:#f2f2c2;
    padding: .5rem;
    margin: .5em auto;

    background-image: var(--lock-mask);
    background-size:contain;
    background-repeat: no-repeat;
    background-position: center top;
}
/*这是电子邮件*/
.email-example .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: #b01; }
}
.email {border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)}
.email-example a.collapsible-block-link {font-weight: bold;}
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px maroon}

/*这是便签纸*/
.page {
    display: block;
    overflow: hidden;
    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    font-style: normal;

    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.page p,
.page ul {
    line-height: 20px;
    margin: 0;
}
/*这是附录标题*/
.dark-title {
    color: rgb(var(--dark-accent));
    font-family: 'Noto Serif SC',serif;
    text-align: center;
    font-size: x-large;
    font-weight: 900;
}
.dark-title:before {
    color: rgb(var(--dark-accent));
    content:var(--dot);
    font-size: x-large;
    font-weight: 900;
}
[[/module]]
