[[include :scp-wiki-cn:theme:basalt]]
[[module css]]
@import url('https://fonts.font.im/css?family=Miss Fajardose');
:root {
    --basalt-primary-color: 15,15,15;
    --basalt-secondary-color: 20,20,20;
    --basalt-tertiary-color: 60, 100, 120;
    --basalt-main-text-color: 247,248,250;
    --basalt-overtone: 254,254,254;
    --basalt-undertone: 60, 100, 120;

    --basalt-bright-element-color: 174, 195, 252;
    --basalt-dark-element-color: 52, 96, 124;
    --basalt-alternate-color: 60, 100, 120;
    --basalt-positive-color: 24,163,20;
    --basalt-negative-color: 255,48,48;
    --basalt-cancel-color: var(--basalt-negative-color);
    --basalt-sub-text-color: 155,154,162;
    --basalt-light-text-color: var(--basalt-primary-color);

    --basalt-UI-dark-palette: var(--basalt-overtone);
    --basalt-focus-color: var(--basalt-undertone);
    --basalt-background-color: var(--basalt-primary-color);
    --link-newpage-color: 204,240,255;
}
@media only screen and (min-width: 767px) {
.mobile {
    display: none;
}
}
:root:lang(cn) {
    --header-title: '监督者指挥部';
    --header-subtitle: '控制-收容-保护';
    --header-logo: url('https://scp-wiki.wdfiles.com/local--files/theme%3Athe-way-out/wtf.svg');
}
#page-content :is(.image-block,.scp-image-block), #page-content :is(.image-block,.scp-image-block)>* {
    box-shadow: 0 0 5px 0px black;
}
:is(.image-block,.scp-image-block).block-center {
    margin: 0;
}
.table2 {
    z-index: 1;
    position: relative;
    float: right;
}
#header h1 a::before {
    mix-blend-mode: difference;
}
body {
    background-color: rgb(var(--basalt-tertiary-color));
    background-image: linear-gradient(180deg, rgba(0, 0, 0,.9) 0%, rgba(10, 10, 10,.7) 50%, rgba(0, 0, 0,.9) 100%);
    background-attachment: fixed;
    background-size: cover;
    background-blend-mode: luminosity;
}
/*这是div板式*/
.anom-bar-container.clear-6 .top-center-box > div {
    background-color: rgba(130,130,130,.8) !important;
    filter: sepia(1) hue-rotate(180deg) contrast(1.5);
}
.main-class {
    filter: sepia(1) hue-rotate(180deg) contrast(1.2) saturate(1.5);
}
.danger-diamond {
    filter: sepia(1) hue-rotate(180deg) saturate(3);
}
.overwatch_memo {
    background-image: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/O5_DARKLOGO.png) !important;
    background-color: rgb(26,26,28) !important;
    color: rgb(252 252 252);
    border-color: rgb(227 227 227) !important;
}
.document-main {
    background-color: rgb(var(--basalt-dark-element-color), 0.4);
    padding: 1rem 0.5em 0em 0.5em;
    margin-top: 1rem;
    border: 0.0625rem #CCCCCC solid;
    border: 0.0625rem rgba(var(--basalt-bright-element-color),.4) solid;
    position: relative;
}
.procedure {
    display: block;
    width: -webkit-calc(100% - 3rem);
    width: -moz-calc(100% - 3rem);
    width: calc(100% - 3rem);
    background-color: rgb(var(--basalt-alternate-color), 1);
    padding: 0.75rem 0.75rem 0.5rem;
    margin: 1.5rem 0.5rem 0.5rem 0.5rem;
    border: 0.25rem rgb(var(--basalt-bright-element-color)) solid;
    position: relative;
    line-height: 1.5;
    overflow-wrap: break-word;
    word-wrap: break-word;
    -webkit-hyphens: auto;
    -ms-hyphens: auto;
    -moz-hyphens: auto;
    hyphens: auto;
    font-size: inherit;
}
.second {
    display: block;
    width: -webkit-calc(100% - 3rem);
    width: -moz-calc(100% - 3rem);
    width: calc(100% - 3rem);
    background-color: rgb(var(--basalt-alternate-color), 1);
    padding: 0.75rem 0.75rem 0.5rem;
    margin: 1.5rem 0.5rem 0.5rem 0.5rem;
    border: 0.25rem rgb(var(--basalt-bright-element-color)) solid;
    position: relative;
    line-height: 1.5;
    overflow-wrap: break-word;
    word-wrap: break-word;
    -webkit-hyphens: auto;
    -ms-hyphens: auto;
    -moz-hyphens: auto;
    hyphens: auto;
    font-size: inherit;
}
.third {
    display: block;
    width: -webkit-calc(100% - 3rem);
    width: -moz-calc(100% - 3rem);
    width: calc(100% - 3rem);
    background-color: rgb(var(--basalt-alternate-color), 1);
    padding: 0.75rem 0.75rem 0.5rem;
    margin: 1.5rem 0.5rem 0.5rem 0.5rem;
    border: 0.25rem rgb(var(--basalt-bright-element-color)) solid;
    position: relative;
    line-height: 1.5;
    overflow-wrap: break-word;
    word-wrap: break-word;
    -webkit-hyphens: auto;
    -ms-hyphens: auto;
    -moz-hyphens: auto;
    hyphens: auto;
    font-size: inherit;
}
.document-main > .procedure::before {
    content: "特殊收容措施";
}

.document-main > .second::before {
    content: "描述";
}
.document-main > .third::before {
    content: "参考信息";
}
.document-main > div.procedure::before, .document-main > div.second::before, .document-main > div.third::before {
    background-color: rgb(68 39 110);
    padding: 0.5em 0.5em;
    position: absolute;
    top: -1.25rem;
    left: -0.5rem;
    outline: 3px solid #aec3fc;
}
/*这是电子邮件*/
.email-example .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: #b01; }
}
.email {
    border: solid 2px #fff;
    width: 88%;
    padding: 1px 15px;
    margin: 10px auto;
    box-shadow: 0 1px 3px rgba(0,0,0,.5)
}
.email-example a.collapsible-block-link {font-weight: bold;}
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px rgb(var(--basalt-tertiary-color));}
/* 对话*/
.interview{
    border-top:solid 2px #0C2436;
    border-bottom:solid 2px #0C2436;
    background: #708090;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

