[[include :scp-wiki-cn:theme:black-highlighter-theme]]
[[include :scp-wiki-cn:theme:extra-black-highlighter-theme]]
[[html]]
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
body {font-family: Verdana;
text-align: center;

}
* {box-sizing: border-box;}

.input-container {
    display: -ms-flexbox; /* IE10 */
    display: flex;
    width: 100%;
    margin-bottom: 15px;

}

.input-field:focus{
border-style: solid;
transition: 0.5;
border-color: maroon;
border-width: 2px;

}

.input-field {
    width: 100%;
    padding: 10px;
    outline: none;
    transition: 0.5s;

}
.input-field2 {
    width: 100%;
    padding: 10px;
    outline: none;
    transition: 0.5s;

}

.btn:focus {
  outline: 0;
}

.btn {
  background-color: Maroon;
  color: white;
  padding: 10px 20px;
  border: none;
  cursor: pointer;
  width: 50%;
  opacity: 0.9;
  transition: 0.09s;
  border-radius: 3px;
}

.btn:hover {
  opacity: 1;
  transition: 0.8s;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.7);
}

.btn:active {
  transition: 0.09s;
  transform: translateY(1px);
  box-shadow: 0 0px 0px 0 rgba(0, 0, 0, 0);
}

.btn2 {
    background-color: white;
    border: none;
    cursor: pointer;

}

p{
font-family: Helvetica;
margin-bottom: 5%;
}
</style>
</head>
<body>

<form autocomplete="off" style="max-width:450px;text-align:center;margin: auto;">
<p style="font-size:30px; font-weight: bold; color: red;" >请确认你的权限......</p>
  <div class="input-container">
    <input class="input-field" type="text" placeholder="用户名" name="usrnm" value="O5-13" readonly>
  </div>

  <div class="input-container">
    <input class="input-field2" value="" type="text" readonly>

  </div>
<button id="enter" onclick="window.open('https://scp-wiki-cn.wikidot.com/scp-cn-3902/offset/1','_parent')"> 登入</button>
</form>
</body>

 <script>
        document.addEventListener('DOMContentLoaded', function() {
            const text = "FoundationForever";
            const target = document.querySelector('.input-field2');

            let index = 0;

            function typeWriter() {
                if (index < text.length) {
                    target.value += text.charAt(index);
                    index++;
                    setTimeout(typeWriter, 280); // 控制打字速度
                } else {
                    setTimeout(replaceContent, 500); // 文字全部显示后等待1秒
                }
            }

            function replaceContent() {
                target.value = '●'.repeat(text.length); // 替换内容为●
            }

            typeWriter(); // 启动函数
        });
document.getElementById('enter').addEventListener('click', function() {

    var elements = document.body.getElementsByTagName('*');

    // 遍历所有元素并隐藏
    for (var i = 0; i < elements.length; i++) {
        if (elements[i].tagName !== 'BODY' && elements[i].tagName !== 'SCRIPT') { // 避免隐藏<body>标签和<script>标签

        }
    }
});
    </script>
[[/html]]
