[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300;400;700;900&display=swap');

div#page-content div.content > p:nth-child(1) {
    text-indent: 0;
}

div#page-content div.content > p:nth-child(1)::first-letter {
    margin-top: 0.5rem;
    float: left;
    font-size: 300%;
    line-height: 100%;
    font-family: "Noto Serif SC";
    font-weight: 900;
}

div#page-content div.content div[style*="align"] p {
    text-indent: 0;
}

div#page-content div.content p {
    text-indent: 2em;
}

/* 标题样式来自单色型版式 */

div#page-content h1 {
    font-size: 1.5rem;
    color: var(--ur_redstone);
    margin-top: 0.6rem;
    margin-bottom: 0.6rem;
    display: flex;
}

div#page-content h1 * {
    font-family: "Noto Serif SC";
    font-weight: 900;
}

div#page-content h1::before {
    content: "";
    flex-grow: 1;
    height: 0.1rem;
    margin: auto 0.375rem auto auto;
    background-color: #FFF;
}

div#page-content h1::after {
    content: "";
    flex-grow: 1;
    height: 0.1rem;
    margin: auto auto auto 0.375rem;
    background-color: #FFF;
}

.image-container {
    margin: 0;
    padding: 0;
}

.image {
    border: solid 1px #FFF;
}

div#page-content .blockquote {
    margin-left: 0;
    margin-right: 0;
    border-radius: 1rem;
}

div#page-content .blockquote h1::before,
div#page-content .blockquote h1::after {
    background-color: transparent;
}

.blockquote .image {
    border: none;
}
[[/module]]

[[include :scp-wiki-cn:theme:black-highlighter-theme]]
[[include :scp-wiki-cn:theme:jakstyle]]

[[module CSS]]

div.page-rate-widget-box .rate-points {
    border: none;
}

@media (max-width: 768px) {
    #content-wrap {
        grid-template-columns: none;
    }
}

div.blockquote.warning-panel {
    width: 30rem;
    max-width: 80% !important;
    margin-left: auto;
    margin-right: auto;
}

div.blockquote.warning-panel * {
    max-width: 100% !important;
    position: relative;
    z-index: 2;
}

div.blockquote.warning-panel::before {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/hormress-s-logos/ARED_2_i.svg");
    background-repeat: no-repeat;
    background-position: center center;
    background-size: 80% auto;
    opacity: 0.1;
    z-index: 1;
}

div.blockquote.function {
    display: flex;
    align-items: center;
}

div.blockquote.function > div.image {
    flex-shrink: 0;
    flex-grow: 0;
    margin-right: 1em;
    opacity: 0.2;
}

div.blockquote.function > div.image a {
    box-shadow: none !important;
}

div.blockquote.function > div.content {
    flex-shrink: 1;
    flex-grow: 1;
}

div.scp-image-block,
div.blockquote {
    box-shadow: 0 0 0 0.125rem rgb(var(--dark-accent));
}

div.blockquote.warning-panel {
    position: relative;
    width: 30rem;
    max-width: 80% !important;
    margin-left: auto;
    margin-right: auto;
    text-align: center;
}

div.blockquote.warning-panel * {
    max-width: 100% !important;
    position: relative;
    z-index: 2;
}

div.blockquote.warning-panel::before {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-image: url("http://czyesperanza.wdfiles.com/local--files/desk-of-junior-designer-s-yvonne/RAISA");
    background-repeat: no-repeat;
    background-position: center center;
    background-size: 80% auto;
    opacity: 0.1;
    z-index: 1;
}


span.gtitle {
    font-size: 120%;
    font-weight: 900;
}

div.blockquote.rec {
    width: 75%;
    margin: 1rem auto;
}

div.blockquote.rep {
    border-radius: 1rem;
    margin: 1rem 0;
}

