[[module CSS]]

#page-content .lizard {
text-align: center;
font-family: var(--mono-font);
font-size: 1.25em;
font-weight: bold;
color: rgb(255，0，0) !important;
}

#page-content div.creditRate {
    margin-right: 0;
}

#page-content table.myHeader {
    border: none;
    width: 100%;
}

#page-content div.myImageBlock {
    margin: 2em 0;
    width: 100%;
    border: solid 1px #666;
    background-color: #FAFAFA;
    box-shadow: 0 1px 6px rgba(0,0,0,0.25);
}
#page-content div.myImageBlock img {
    width: 100%;
}
#page-content div.myImageBlock div.myImageCaption {
    text-align: center;
    border-top: solid 1px #666;
}
#page-content div.myImageBlock div.myImageCaption p {
    margin: 4px 1em;
    padding: 0;
}

#page-content div.myBasicBlock {
    border-radius: 8px;
    border: solid 1px #666;
    background: #FAFAFA;
    box-shadow: 0 1px 6px rgba(0,0,0,0.25);
    box-sizing: border-box;
    width: 100%;
    padding: 15px;
    margin: 1em 0;
    display: inline-block;
}

#page-content div.myBlockquote {
    margin: 1.5em 40px;
    padding: 0 1em;
    border: dashed 1px #666;
}
@media (max-width: 767px) {
    #page-content div.myBlockquote {
        margin: 1.5em 0;
    }
}

#page-content span.redacted {
    color: #333;
    background-color: #333;
    user-select: none;
}

#page-content div.myBasicBlock hr {
    margin-left: 0;
}

#page-content tt {
    color: #ffffff;
}

#page-content .memo {
    background-color: #2D2D2D !important;
}

#page-content div.myBasicBlock div.comm div.aaron {
    text-align: right;
    color: #901;
}

#page-content div.myBasicBlock div.comm p {
    padding-right: 38%;
}
#page-content div.myBasicBlock div.comm div.aaron p {
    padding-left: 38%;
    padding-right: 0;
}

#page-content div.comm p::after {
    content: "↲";
}

[[/module]]

[[include :scp-wiki-cn:theme:black-highlighter-theme]]
[[include :scp-wiki-cn:theme:ouroborous-theme]]
[[include :scp-wiki:component:centered-header-bhl]]

[[module css]]

:root:lang(cn) {
  --logo-image: url("unset");
  --header-title:"SCP基金会";
  --header-subtitle:"";
}

#header h1 a::before  {  
  display: none
}

#header h1 a::after  {
  font-weight: 600;
  letter-spacing: 4px;

}

.lore{
color:#12050a
}

.comanda{
  background: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aouroborous-theme/ouroborous_logo.png);
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  text-align: center;
  width: 600px;
  margin:6em auto;
  font-size: 120%;
  padding: 0px;
  width: 100%
}

#page-content div,
#page-content table,
#page-content * {
    max-width: 100%;
}

[[/module]]

[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300;400;700;900&display=swap');

div#page-content div.content > p:nth-child(1) {
    text-indent: 0;
}

div#page-content div.content > p:nth-child(1)::first-letter {
    margin-top: 0.5rem;
    float: left;
    font-size: 300%;
    line-height: 100%;
    font-family: "Noto Serif SC";
    font-weight: 900;
}

div#page-content div.content div[style*="align"] p {
    text-indent: 0;
}

div#page-content div.content p {
    text-indent: 2em;
}

/* 标题样式来自单色型版式 */

div#page-content h1 {
    font-size: 1.5rem;
    color: var(--ur_redstone);
    margin-top: 0.6rem;
    margin-bottom: 0.6rem;
    display: flex;
}

div#page-content h1 * {
    font-family: "Noto Serif SC";
    font-weight: 900;
}

div#page-content h1::before {
    content: "";
    flex-grow: 1;
    height: 0.1rem;
    margin: auto 0.375rem auto auto;
    background-color: #FFF;
}

div#page-content h1::after {
    content: "";
    flex-grow: 1;
    height: 0.1rem;
    margin: auto auto auto 0.375rem;
    background-color: #FFF;
}

.image-container {
    margin: 0;
    padding: 0;
}

.image {
    border: solid 1px #FFF;
}

div#page-content .blockquote {
    margin-left: 0;
    margin-right: 0;
    border-radius: 1rem;
}

div#page-content .blockquote h1::before,
div#page-content .blockquote h1::after {
    background-color: transparent;
}

.blockquote .image {
    border: none;
}
[[/module]]

