[[html]]
<body>
<p>
<div class="text" style="padding: 1em;
     color: hsl(120, 25%, 25%);
     background-color: transparent;
    @import url('');
     text-align: center;
      height:200px;
      width: auto;
"></div>
</p>
</body>
<style>

@font-face {
    font-family: "GlowSansSC";
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/GlowSansSCRegular_3500.woff2')
}
.text {
    font-family: "GlowSansSC", system-ui;
    font-size: 30px;
}
</style>
<script type="text/javascript">
obfu_data = {
"delay":2000,
"start_time":500,
"end_time":700,
"disp_time":2000,
"loop":true,
"obfu_chars":"丶一丨丿刂十▞▇▕012阝土宀/6➸⣾█♟≷⫉㇏[■ⓓ⯯Ⲥ⼃⓷⎳▀▛▁▋]{}—=+*^?#▰Ⰻ⌷∬⮡⩼ⓚ⧪❾⨶⁉⁼ℵ▜▆░▙ⅵ➡ℨ⏎⪒⛴⥹心╞⊤✴亅木▟34女口▞⽄▖▍⋝⓿Ⓝ⅞␔⻯━⤐➣Ⲓⶬ日文⸰⍾⎨⪃⑑⺪⠁⼏⧝ℸ⸧ↈⅇ⓳⠪⫧✜⢖ℾⱷ⽠₤‒⅟▉▚▒▗▂▄▐ⱶ⭬▭⼆匕犭马⼣羊█！白矢▝5㇀亻ⱺ▓讠又<>-_\▘▊▔\/[]{}—=+*^？#<>-_\\扌辶789█!▅▃",
"phrases":[
'系统发生严重错误。请勿继续访问。','系統遭受異常攻擊','','如果宿命的意義是死亡，那這也會是我們的意義。','耶和華憎惡撒誑的唇，祂曾經亦如此警示，故而那些不入天堂的魔鬼，世人不應予以其同等之信任，否則始端便將是終結，宿命便定成敗局。','敗局。敗局。敗局。敗局。敗局。敗局。敗局。敗局。',
]
}
</script>
<script type="text/javascript">
var obfuData = obfu_data;

const delay = obfuData.delay
const start_time = obfuData.start_time
const end_time = obfuData.end_time
const disp_time = obfuData.disp_time
const loop = obfuData.loop
const obfu_chars = obfuData.obfu_chars
const phrases = obfuData.phrases

class TextScramble {
  constructor(el) {
    this.el = el
    this.chars = obfu_chars
    this.update = this.update.bind(this)
  }
  setText(newText) {
    const oldText = this.el.innerText
    const length = Math.max(oldText.length, newText.length)
    const promise = new Promise((resolve) => this.resolve = resolve)
    this.queue = []
    for (let i = 0; i < length; i++) {
      const from = oldText[i] || ''
      const to = newText[i] || ''
      const start = Math.floor(Math.random() * start_time)
      const end = start + Math.floor(Math.random() * end_time)
      this.queue.push({ from, to, start, end })
    }
    cancelAnimationFrame(this.frameRequest)
    this.frame = 0
    this.update()
    return promise
  }
  update() {
    let output = ''
    let complete = 0
    for (let i = 0, n = this.queue.length; i < n; i++) {
      let { from, to, start, end, char } = this.queue[i]
      if (this.frame >= end) {
        complete++
        output += to
      } else if (this.frame >= start) {
        if (!char || Math.random() < 0.28) {
          char = this.randomChar()
          this.queue[i].char = char
        }
        output += `<span class="dud">${char}</span>`
      } else {
        output += from
      }
    }
    this.el.innerHTML = output
    if (complete === this.queue.length) {
      this.resolve()
    } else {
      this.frameRequest = requestAnimationFrame(this.update)
      this.frame++
    }
  }
  randomChar() {
    return this.chars[Math.floor(Math.random() * this.chars.length)]
  }
}

const el = document.querySelector('.text')
const fx = new TextScramble(el)

let counter = 0
const next = () => {
  fx.setText(phrases[counter]).then(() => {
    setTimeout(next, disp_time)
  })
  if (!loop & counter < phrases.length) {
    counter = counter + 1;
  } else if (counter <= phrases.length) {
    counter = (counter + 1) % phrases.length;
  }
}

setTimeout(next, delay)

</script>
[[/html]]
