[[module CSS]]
@import url(http://scp-wiki-cn.wikidot.com/component:4sc-theme/code/1);
[[/module]]

[[module css]]
#header {
    height: 120px;
    position: relative;
    z-index: 10;
    padding-bottom: 25px; /* FOR MENU */
    background-image:url(http://scpsandboxcn.wikidot.com/local--files/tinykeng2/%E5%9B%9B%E6%98%9F%E5%85%B1%E8%8D%A3%E5%9C%A3%E8%A3%81%E9%83%A82.png);
    background-repeat:no-repeat;
    background-position:10px 5px; 
}

#header h1 {
    margin-left: 170px;
    padding: 0;
    float: left;
    max-height: 98px;
}
#header h2 {
    margin-left: 250px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 60px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family:'Franklin Gothic Medium', Arial, sans-serif;
    font-size: 180%;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 1px;
}

#header h1 a::before {
    content: "四星共栄聖裁部";
    color: #fff;
    text-shadow: 4px 4px 8px #000000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 12px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '　絶罪　 絶惑　 絶意　';
    color: #000;
    font-style: normal;
    text-shadow: 1px 2px 4px #fff;
}

#page-title {
  display: none;
}

/* Mobile Media Query */
@media (max-width: 479px) { 
#search-top-box-input {
    display: none;
}

#page-content {
    font-size: 0.9em;
}

#main-content {
    margin: 0;
}

#recent-posts-category {
width: 100%;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

#side-bar {
    width: 80%;
    position: relative;
}

.top-bar {
    display:none;
}

.mobile-top-bar {
    display: block;
    padding: 0;
}

.page-options-bottom a {
    padding: 0 4px;
}

#header h1 a {
    font-size: 100%;
}

blockquote {
    margin: 1em 0;
}

.license-area {
    font-size: 0.8em;
}

#header {
    background-position: 0 5.5em;
    background-size: 55px 55px;
}

#header h1, #header h2 {
    margin-left: 66px;
}

table.form td, table.form th {
    float: left;
}

/*
td.title {
    width: 30%;
}
*/

td.name {
    width: 15em;
}

table.form td, table.form th {
    padding: 0;
}

#edit-page-title {
    max-width: 90%;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}

#page-content div.title {
word-break: keep-all;
}

}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) { 
#search-top-box-input {
    width: 7em;
}

#main-content {
    margin: 0 2em 0 2em;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

#side-bar {
    width: 80%;
    position: relative;
}

.top-bar {
    display:none;
}

.mobile-top-bar {
    display: block;
}

.page-options-bottom a {
    padding: 0 5px;
}

#header h1 a {
    font-size: 120%;
}

blockquote {
    margin: 0.5em;
}

.license-area {
    font-size: 0.85em;
}

#header {
    background-position: 0.5em 4.5em;
    background-size: 66px 66px;
}

#header h1, #header h2 {
    margin-left: 80px;
}

/*
td.title {
    width: 30%;
}
*/

#page-content div.title {
word-break: keep-all;
}

td.name {
    width: 15em;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) { 
#search-top-box-input {
    width: 8em;
}

#side-bar {
    width: 80%;
    position: relative;
}

#main-content {
    margin: 0 3em 0 2em;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

.top-bar {
    display: none;
}

.mobile-top-bar {
    display: block;
}

.page-options-bottom a {
    padding: 0 6px;
}

#header h1 a {
    font-size: 140%;
}

.license-area {
    font-size: 0.9em;
}

#header {
    background-position: 1em 4em;
    background-size: 77px 77px;
}

#header h1, #header h2 {
    margin-left: 93px;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#main-content {
    margin: 0 4em 0 20em;
}

#header, #top-bar #side-bar {
    max-width: 100%;
}

.top-bar li {
    margin: 0;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    width: 110px;
}

.page-options-bottom a {
    padding: 0 7px;
}

#header h1 a {
    font-size: 160%;
}

.license-area {
    font-size: 0.95em;
}

#header {
    background-position: 1em 4em;
    background-size: 88px 88px;
}

#header h1, #header h2 {
    margin-left: 106px;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}
}

/* Desktop Media Query -----------
@media (min-width: 980px) and (max-width: 1399px) {

}
------------------------------------------ */

/* Wide Monitor Media Query -----
@media (min-width: 1400px) {

}
------------------------------------------ */

/* off-canvas */

.close-menu {
    display: none;
}

@media (max-width: 767px) {

.page-history tbody tr td:last-child {
width: 35%;
}

.owindow {
    min-width: 80%;
    max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
    float: left;
}

.owindow .button-bar {
    float: right;
}

.owindow div .btn-primary {
width: 100%;
float: left;
}

.owindow div .btn-primary ~ div {
width: 100%;
}

.yui-navset {
z-index: 1;
}

#navi-bar, #navi-bar-shadow {
    display: none;
}

.open-menu a {
    position: fixed;
    top: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border: 0.2em solid #888 !important;
    background-color: #fff !important;
    border-radius: 3em;
    color: #888 !important;
}

.open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
}

#main-content {
    max-width: 90%;
    margin: 0 5%;
    padding: 0;
    -webkit-transition: 0.5s ease-in-out 0.1s;
    -moz-transition: 0.5s ease-in-out 0.1s;
    -ms-transition: 0.5s ease-in-out 0.1s;
    -o-transition: 0.5s ease-in-out 0.1s;
    transition: 0.5s ease-in-out 0.1s;
}

#side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 17em;
    height: 100%;
    background-color: rgb(184, 134, 134);
    overflow-y: auto;
    z-index: 10;
    padding: 1em 1em 0 1em;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
    content: "";
    position: absolute;
    top: 0;
    width: 0;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);

}

#side-bar:target {
    display: block;
    left: 0;
    width: 17em;
    margin: 0;
    border: 1px solid #dedede;
    z-index: 10;
}

#side-bar:target + #main-content {
    left: 0;
}

#side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat;
    z-index: -1;
}
}
[[/module]]

[[module CSS]]

div.terminal{
    border: 1px solid black;
    border: solid 3px #BBBBBB;
    border-radius: 16px;
    background-color: #131;
/* 端末のグラデーション */
    background-image: 
        radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #121);
    background-position: center;
    display: block;
/* 端末周辺の影 */
    box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
/* スキャン演出によるスクロールバーの非表示 */
    overflow:hidden;
}
div.terminal blockquote {
    background-color: black;
    border: double 3px #80FF80
}
div.scanline{
    margin-top: -40%;
    width: 100%;
    height: 60px;
    position: relative;
    pointer-events: none;
/* Safari 4.0 - 8.0 */
    -webkit-animation: scan 7s linear 0s infinite; /* スキャン演出の動きが早すぎた場合、5秒ほど長くしてください。 */
    animation: scan 7s linear 0s infinite; /* 同上 */
    background: linear-gradient(to bottom, rgba(56, 112, 82,0), rgba(56, 112, 82,0.1)) !important;
}

div.text{
    color: rgba(128,255,128,0.8);
    padding-left: 2em;
    padding-top: 40%;
    font-family: monospace;
    font-size: 1.2em;
    }

/* Safari 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* ターミナルの長さに応じて変更可。スキャン演出が半分しか表示されない場合は2番目の数字を大きくすること。 */
}

@keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* 同上。 */
}

div.text a {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a.newpage {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a:hover {
    color: #131;
    text-decoration: underline;
    background-color: #80FF80;
    padding: 1px;
}
div.text a:hover::before{
content: "> ";
}
