[[module CSS]]

#header h1 {
    margin-left: 13%;
    padding: 0;
    float: left;
    max-height: 90px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-size: 120%;
    font-family: YouYuan;
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
    content: "废墟：Site-CN-20";
    color: #FFFFFF;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 {
    margin-left: 13%;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '我将发掘并见证，于最初生长在此的疯狂。';
    color: #FFFFFF;
    text-shadow: 1px 1px 1px #000000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
[[/module]]

[[module CSS]]
.footnotes-footer {
display: none;
}
[[/module]]
