[[module CSS]]
#page-title {
  display: none;
}

.optionstd a:nth-child(2),
.optionstd a:nth-child(3){
  display: none;
}

.meta-title {
  border-bottom: solid 1px #bbbbbb;
  color: #990011;
  font-weight: normal;
  margin: 0 0 0.6em;
  padding: 0 0 0.25em;
  font-size: 80px;
}

.meta-title p {
  margin: 0;
}
#u-ratingboxhid {   
 opacity:0.05;  transition: all 0.5s ease-out; 
}
#u-ratingboxhid:hover {  
 opacity:1;
}

.page-source::before {
  content: '  \A       [ DATA CORRUPTION ]  \A ';
   color: lightgreen ;
   font-size:30px;
   text-align:center;
   white-space: pre-wrap;  
font-family:'等线',dengxian,'幼圆';
}

.sub::before {
 content: 'Decommissioned';
 color:#b01;
}



#who-rated-page-area::before {

content: "⊙⊙⊙⊙⊙⊙";
color: #b01;

}

#revision-list::before{
content: ' 世界已经重构了多少次？';
font-size:200%;
}

 .page-tags a::before{
content: '⚠️';
font-size:105%;
}


.page-source{
  height:100px;
  background: black;
  border:double  5px #BCB7B6;
  font-size:0em;
  color: transparent;  	
}

 a{
 color:#5a5757;
}

a::visited {
 color:grey ;
}

#page-options-bottom a::hover{
text-decoration:line-through;
 color:#b01 ;
}

.page-history .printuser::before{
 content:url(http://www.wikidot.com/common--images/avatars/default/a16.png);
}


.page-history .printuser::after{
 content:" (account deleted) ";
 font-size:1rem;
  margin: 0 0rem 0  0.5rem;
}


.page-history td:not(span) {
 color: transparent;  
}

.page-history td span {
   color: #333;
}

.page-history .printuser{
font-size:0em;
}
.page-history .printuser img{
display:none
}

[[/module]]
[[module ListPages category="fragment" parent="." limit="1" order="name" offset="@URL|0"]]
[[div class="meta-title"]]
%%title%%
[[/div]]
[[/module]]
[[>]]
[[span id="ratingboxhid"]]
[[module Rate]]
[[/span]]
[[/>]]
