[[div style="background: url(/local--files/protected%3Apage/SCP%20Logo%2035.png) center center no-repeat ; float: center; background-size: 90%;"]]
[[html]]
<head>
<link href='//fonts.googleapis.com/css?family=Aldrich' rel='stylesheet'>
<style>
body {
  background: #456;
  font-family: verdana;font-size: 12.8px;
}

robot {
font-family: Aldrich;font-size: 12.8px;
}

.login {
  width: 400px;
  margin: 16px auto;
  font-size: 16px;
}

.login-header,
.login p {
  margin-top: 0;
  margin-bottom: 0;
}

.login-header {
  background: #962407;
  padding: 20px;
  font-size: 1.4em;
  font-weight: normal;
  text-align: center;
  text-transform: uppercase;
  color: #fff;
}

.myForm {
  background: #ebebeb;
  padding: 12px;
}

.login p {
  padding: 12px;
}

.login input {
  box-sizing: border-box;
  display: block;
  width: 100%;
  border-width: 1px;
  border-style: solid;
  padding: 16px;
  outline: 0;
  font-family: inherit;
  font-size: 0.95em;
}

.login input[type="email"],
.login input[type="password"] {
  background: #fff;
  border-color: #bbb;
  color: #555;
}

.login input[type="fnumber"]:focus,
.login input[type="fname"]:focus {
  border-color: #888;
}

.login input[type="submit"] {
  background: #962407;
  border-color: transparent;
  color: #fff;
  cursor: pointer;
}

.login input[type="submit"]:hover {
  background: #721902;
}

/* Buttons' focus effect */
.login input[type="submit"]:focus {
  border-color: #05a;
}
</style>

<div class="login">
  <br>
  <h2 class="login-header"><img src="http://vignette2.wikia.nocookie.net/mlp/images/7/78/SCP_Foundation_Logo_white.png/revision/latest/scale-to-width-down/480?cb=20150517110821" style="width:125px;height:125px;">
<br>
访问终端</h2>


  <form name="myForm" action="demo_form.asp"
onsubmit="return validateForm()" method="post">
    <p><input type="text" name="fnumber" placeholder="用户名"></p>
    <p><input type="text" name="fname" placeholder="六位密码"></p>
    <p><input type="submit" value="确认"></p>
  </form>
<br>
<center><robot  id="demo"></robot></center>
</div>

<script>
function validateForm() {
    var x, text;
    var y, text;

    x = document.forms["myForm"]["fname"].value;
    y = document.forms["myForm"]["fnumber"].value;
    if (y == "j.ashworth" && x == "070907") {
       window.open('http://scp-wiki-cn.wikidot.com/scp-es-100/offset/3');
       return false;
    } else if (y == "j.ashworth") {
       document.getElementById("demo").innerHTML = "密码帮助<br><br> <i>我家小Émili的那一天</i>";
       return false;
    } else {
       document.getElementById("demo").innerHTML = "用户名或密码错误。";
       return false;
    }
}
</script>
</head>
<body>

</body>
[[/html]]

