[[module css]]
:root {
  --title: "辉光";
  --subtitle: "内心无形的太阳";
  --logo: url(http://pokm.wdfiles.com/local--files/form-star/huiguang);
}

#breadcrumbs {
margin-top: 4em;
}

.no {
  color: transparent;
  text-shadow: none;
  letter-spacing: -1px;
}

.fncon {
  background: #ccc !important;
  color: #111;
}

.custom2 table {
    width: 100%;
}

.custom2 tr td {
    padding: 10px !important;
}

#page-content .lizard {
    background-color: #111 !important;
    font-size: 150%;
    text-align: center;
    border: 3px #eee double;
    color: #f24;
    font-family: monospace;
    font-weight: 900;
}

body {
    background-color: #380004;
    background-image: url("https://scp-wiki.wdfiles.com/local--files/component%3Aanomaly-class-bar/galactic.jpg");
    background-attachment: fixed;
    background-size: contain;
    background-blend-mode: overlay;
}

body:before {
  content: ' ';
  position: fixed;
  display: block;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background-image: linear-gradient(to bottom, transparent, #0c0c0c 75%, #0c0c0c 75%, #0c0c0c 100%);
  z-index: -2;
}

hr {
background: transparent;
border-top: double 3px #b10009;
}

.notation hr, .jotting hr, .darkdocument hr, .modal hr {
border-top: double 3px grey;
}

#extra-div-1 {
background: linear-gradient( 180deg, rgb(10, 10, 10) 0%, rgb(60, 0, 0) 100%);
box-shadow: 0px 0px 8px 4px rgb(0 0 0);
}

#side-bar {
    background: linear-gradient(
0deg
 , rgba(10, 10, 10, 1) 87%, rgb(27 5 5) 100%);
}

a {
    color: #ff3838;
    font-weight: 600;
}

a.newpage {
    color: #ff6b6b;
    font-weight: 600;
}

a:visited {
    color: #ff1414;
    font-weight: 600;
}

#side-bar .side-block.resources {
    background: rgb(10, 0, 1);
}

#side-bar .side-block.media {
    background-color: rgb(5 5 5) !important;
    border-bottom: solid 2px rgb(10, 10, 10);
    border-top: solid 2px rgb(10 10 10);
}

::selection {
    background-color: rgba(255, 0, 0, 0.7);
    color: white;
}

/*-- normal image block --*/

.scp-image-blockN {
    border: solid 1px #666;
    box-shadow: 0 1px 6px rgba(0, 0, 0, .25);
    width: 300px;
}

.scp-image-blockN.block-rightN {
    float: right;
    clear: right;
    margin: 0 2em 1em 2em;
}

.scp-image-blockN img {
    border: 0;
    width: 300px;
}

.scp-image-blockN .scp-image-captionN {
    background-color: #eee;
    border-top: solid 1px #666;
    padding: 2px 0;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
    width: 300px;
}

.scp-image-blockN > p {
    margin: 0;
}

.scp-image-blockN .scp-image-captionN > p {
    margin: 0;
    padding: 0 10px;
}
