[[module css]]
/* ----- flex 盒子 ----- */

.flex-box {
    display: -webkit-flex;
    /* Safari */
    display: flex;
    flex-flow: row wrap;
    justify-content: space-between;
}

.flex-box>.box {
    flex-basis: 33%;
}

.float-sticky + br{
  display: none;
}

/*    -----  推荐  ----- */

.semi-intro {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}

.semi-intro div {
    flex-basis: 42%;
    min-width: 40%;
    margin: 1rem;
    flex-grow: 1;
    padding: 0.5rem;
}

.semi-intro .semi-intro-tt {
    margin: 0;
    text-align: center;
}

.mz-tt.tt-box {
    padding-top: 0;
}

.mz-tt.tt-box  h1:first-of-type + * {
    margin-top:-5rem;
}

@media (max-width: 1040px) {
    #page-content .flex-box>.box {
        flex-basis: 49%;
    }
    .semi-intro .semi-intro-tt {
        padding-top: 1rem
    }
}

.box {
    background-color: rgb(var(--pale-gray-monochrome));
    border: 1px solid rgb(var(--dark-accent));
    padding: 0 1em;
    box-sizing: border-box;
    position: relative;
    margin-bottom: 1em;
    color: #182026
}

.box>.collapsible-block .collapsible-block-folded {
    position: absolute;
    bottom: 10px;
}

@media (max-width: 500px) {
    .box>.collapsible-block .collapsible-block-folded {
    position:   initial;
            margin-bottom: 10px;

}

  .box>.collapsible-block{
            margin-bottom: 0.5rem;

}

  #page-content  .flex-box>.box {
        flex-basis: 100%;
    }
}

/* 企划作品  */

.show-list a {
    color: #4e5166 !important;
}

.show-list a:hover {
    color: #e8e8e8 !important;
    background: #4e5166 !important;
}

.show-list {
    margin: 0 20%;
    position: initial;
}

@media (max-width: 900px) {
    .show-list {
        margin: 0;
    }
}

/* 艺术作品 */

.post-img {
    display: flex;
    flex-wrap: wrap;
    align-items: flex-start;
    justify-content: center
}

.column-img .scp-image-block {
}

.mz-img-wrap.img-b3d .scp-image-block .scp-image-caption {
    display: inline-block;
}

@media (max-width: 767px) {
    .column-img .scp-image-block {
        margin: 1rem -0.25rem 0 0 !important;
    }
}

/* ----- flex 头条 ----- */

.mmposter-wrap {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    margin: 0 auto;
    left: 2rem;
    position: relative;
}

.mmposter-cell {
    width: 48%;
    justify-items: center;
}

@media (max-width: 700px) {
    .mmposter-wrap {
        left: 0rem;
    }
    .mmposter-cell {
        width: 100%;
    }
    .mmposter-right {
        margin-top: 2rem;
    }
    .mob-breakkkk {
        display: block
    }

   .mmposter-left .scp-image-block.block-right {
    float: right;
    clear: right;
    margin: 0;

}
}

.mmposter-wrap + .mz-hr {
    padding:0.3rem 0;
}

/*------------- 其他 --------------*/

/* 内容居中 */
.all-center .scp-image-block{
    float: none;
    margin: 0 auto;
}

/*
----------------------------
额外组件
----------------------------
*/

/* --------------  笔记本 --------------  */

.reserve-separator::after, .note-leaf .note-page::after {
    color: var(--page-bg);
}

/* --------------  浮动菜单 --------------  */

#u-float-memu.memu-ch {
    z-index: 1;
}

.memu-ch {
}

#u-float-memu #toc-list{
    text-align:left;
    line-height:1.2rem;
}

#u-float-memu #toc-list div{
padding-top:0.5rem;

}

#u-float-memu a:hover {
    background:#4e5166 !important;
    color: var(--page-link-txt) !important;
}

#u-float-memu a {
    color:#4e5166;
}

#u-float-memu #toc {
    word-break: keep-all;
    box-shadow: none;
}

.rate-num {
    padding: 1rem 0rem 0 1rem;
    left: 1rem;
    position: relative;
}

#u-float-memu .rate-btn {
    display: none;
}

.memu-ch hr {
    border: 0.5px solid #888 !important;
    margin: 1em 2em !important;
}

.memu-ch {
    right: 0.8%;
}

.memu-pg {
    background: var(--page-bg);
}

#u-float-memu a:hover {
    background: none;
    color: #b01;
}

.fm-goto {
    padding-left: 1rem;
}

#u-float-memu #toc #toc-list div {
    margin: 0 0.8rem !important;
}

#u-float-memu #toc {
    max-width: 10rem;
}

#u-float-memu #toc #toc-list div:hover::before,
#u-float-memu #toc #toc-list a::before {
    left: 0rem;
}

.float-sticky .memu-ch{
   right: -14.5rem;
}

/* ---------- 便签纸引用块 ------------ */

.note-tip-wrap blockquote,
.note-tip-wrap div.blockquote,
#toc {
    box-shadow: none;
}

.note-tip-tt-1.note-tip-wrap {
    margin: -1rem 2rem 0.5rem 2rem;
}

@media (max-width: 700px) {
    .note-tip-tt-1.note-tip-wrap {
        margin: 0 auto;
        width: 95%;
    }
    .note-tip-wrap {
        float: initial;
    }
}
[[/module]]

