[[module CSS]]
@import url('https://scp-wiki-cn.wikidot.com/fragment:semimonth-magazine/code/1');


/*
====================
杂志主题扩展：半月刊特供 

自定义项目
====================
*/

#main-content {
    width: {$content-width};
}

div#header h1 a::before {
    content: {$header-title};
}

#header h2::before {
    content: {$header-subtitle};
}

@media (max-width: 767px) {
    div#header h1 a::before {
        content: {$header-title};
        content: {$mob-title};
    }
    div#header h2::before {
        content: {$header-subtitle};
        content: {$mob-subtitle};
    }
}

.flex-box>.box {
    flex-basis: {$news-box-basis};
}

#header-extra-div-1::before {
    background-image: {$logo-image};
}

#side-bar:before {
    background-image: {$logo-sidebar};
}

#side-bar div.side-block:first-child:before {
    content: {$sidebar-content};
}


.column-img  .scp-image-caption,
.column-img .scp-image-block,
.column-img .image{
    width:450px !important;
    width:{$img-width}  !important;
    float: none;
}

/* --------------  浮动菜单 --------------  */

#u-float-memu.memu-ch {
    z-index: {$memu-index};
}

.memu-ch {
    right: {$memu-right};
}


[[/module]]
