[[module css]]

#breadcrumbs {
    display: none !important;
}

input.text {
    border: 1px solid #777;
    margin: 0 1px;
    padding: 1px 2px;
    background-color: #2b2b2b !important;
    color: #fff !important;
}

textarea {
    font-family: verdana,arial,helvetica,sans-serif;
    padding: 2px;
    font-size: 100%;
    border: 1px solid #777 !important;
    background-color: #212121 !important;
    color: #fff !important;
}

.thread-container .post .head {
    padding: 0.5em 1em;
    background: #2d2d2d !important;
    box-shadow: inset 2px 3px 6px rgba(0,0,0,.15);
    border-radius: 5px 5px 0 0;
}

u-log-18, #u-log-17, #u-log-16, #u-log-15, #u-log-14, #u-log-13, #u-log-12, #u-log-11, #u-log-10, #u-log-9, #u-log-8, #u-log-7, #u-log-6, #u-log-5, #u-log-4, #u-log-3, #u-log-2, #u-log-1 {
display: none;
}

#u-view-18, #u-view-17, #u-view-16, #u-view-15, #u-view-14, #u-view-13, #u-view-12, #u-view-11, #u-view-10, #u-view-9, #u-view-8, #u-view-7, #u-view-6, #u-view-5, #u-view-4, #u-view-3, #u-view-2 {
display: none;
}

#u-log-12:target~#u-view-12, #u-log-11:target~#u-view-11, #u-log-10:target~#u-view-10, #u-log-9:target~#u-view-9, #u-log-8:target~#u-view-8, #u-log-7:target~#u-view-7, #u-log-6:target~#u-view-6, #u-log-5:target~#u-view-5, #u-log-4:target~#u-view-4, #u-log-3:target~#u-view-3, #u-log-2:target~#u-view-2, #u-log-1:target~#u-view-1 {
display: block;
 animation:show 3s 1;
-webkit-animation:show 3s 1;
}

#u-log-18:target~#u-view-18, #u-log-17:target~#u-view-17, #u-log-16:target~#u-view-16, #u-log-15:target~#u-view-15, #u-log-14:target~#u-view-14, #u-log-13:target~#u-view-13 {
