div:target~#u-view-18, div:target~#u-view-17, div:target~#u-view-16, div:target~#u-view-15, div:target~#u-view-14, div:target~#u-view-13, div:target~#u-view-12, div:target~#u-view-11, div:target~#u-view-10, div:target~#u-view-9, div:target~#u-view-8, div:target~#u-view-7, div:target~#u-view-6, div:target~#u-view-5, div:target~#u-view-4, div:target~#u-view-3, div:target~#u-view-2, div:target~#u-view-1{
