}

@keyframes show
{
from {opacity:0;}
to {opacity:1;}
}

@-webkit-keyframes show
{
from {opacity:0;}
to {opacity:1;}
}

body {
  background-color: #000;
  font-family:Comfortaa;
}

.cover-box1{
position:Fixed;
overflow: hidden;
position: fixed;
right:5%;
top: 0;
z-index: 1;
}
.news a:hover::before{
content: "More Info";
color: #000;
background:#77EEFF;
padding:5px;
margin-right:10px;
}

.news a:hover{
color: #fff;
}

.news a {
    border-bottom:solid #c0c0c0 1px;
}

.forme .yui-navset .yui-nav {
    --tab-border-color: transparent;
    --box-shadow: transparent;
    text-align: center;
}

.sideTabview .yui-nav {
    float: left;
    background: var(--tabview-side-tabs-background);
    max-height: 65vh !important;
    width: 25% !important;
    overflow: auto;
    border: none !important;
    box-shadow: var(--tabview-side-tabs-box-shadow);
}
.sideTabview .yui-nav {
    min-height: 65vh!important;
    width: 20%!important;
}
.sideTabview .yui-content>div {
    overflow: auto!important;
    padding: 0 12px!important;
    width: 100%!important;
    max-height: 80vh!important;
}
.sideTabview .yui-navset {
    width: 100%!important;
    margin: 10px 0!important;
    overflow: hidden!important;
    box-shadow: var(--tabview-side-box-shadow);
    background: linear-gradient(to right, var(--tabview-side-tabs-background) 25%, var(--tabview-background) 0);
    border: var(--tabview-side-border);
}

@media screen and (orientation: landscape) {
body, #container-wrap-wrap, #container-wrap, #content-wrap, #page-content {
    min-height: unset !important;
}
.page-background {
    position: -webkit-sticky;
    height: 95vh;
    position: sticky;
    top: 0px;
}
#container {
    background-image: linear-gradient(to bottom, rgba(0,0,0,0.6) 0%, rgba(0,0,0,0.7) 50%, rgba(0,0,0,0.9) 100%), url(https://cdn.pixabay.com/photo/2016/07/29/17/42/lost-places-1555076_960_720.jpg);
    background-size:cover;
    background-attachment: fixed, fixed;
    background-position: center;
    position: relative;
}
.col2 {
   display:none;
}
.sideTabview .yui-navset {
    max-height: 80vh;
    min-height:80vh;
}
.list {
    display: flex;
    flex-direction: column;
    height: 80vh;
    overflow: auto;
    margin: 0 auto;
}
.list-flashnews {
    display: flex;
    flex-direction: column;
    height: 80vh;
    overflow: auto;
}
}
.cover{
    display:none;
}

@media screen and (orientation: portrait) {
.page-background {
    position: -webkit-sticky;
    height: 96vh;
    position: sticky;
    top: 0px;
}
body, #container-wrap-wrap, #container-wrap, #content-wrap, #page-content {
    min-height: unset !important;
}
#container {
    background-image: linear-gradient(to bottom, rgba(0,0,0,0.6) 0%, rgba(0,0,0,0.7) 50%, rgba(0,0,0,0.9) 100%), url(https://cdn.pixabay.com/photo/2016/07/29/17/42/lost-places-1555076_960_720.jpg);
    background-size:cover;
    background-attachment: fixed, fixed;
    background-position: bottom;
    position: relative;
}
.col1 {
   display:none;
}
.yui-navset {
    height: 80vh;
}
.list {
    display: flex;
    flex-direction: column;
    min-height: 66vh;
    overflow: auto;
    margin: 0 auto;
    width: 75vw;
    max-height: 66vh;
}
.list-flashnews {
    display: flex;
    flex-direction: column;
    min-height: 66vh;
    overflow: auto;
    width: 75vw;
    max-height: 66vh;
}
.sideTabview .yui-navset {
    max-height: 65vh!important;
    min-height: 65vh!important;
}
.sideTabview .yui-content>div {
    overflow: auto!important;
    padding: 0 12px!important;
    width: 100%!important;
    max-height: 65vh!important;
}
}

/* customizable snowflake styling */
.snowflake {
  color: #fff;
  font-size: 2em;
  font-family: Arial, sans-serif;
  text-shadow:none;
  opacity:0.45;
}

@-webkit-keyframes snowflakes-fall{
0%{top:100%;opacity:0.45;}
95%{opacity:0.45;}
100%{top:3%;opacity:0;}
}

@keyframes snowflakes-fall{
0%{top:100%;opacity:0.45;}
95%{opacity:0.45;}
100%{top:3%;opacity:0;}
}

.snowflake{
position:fixed;
top:100%;
z-index:9999;
-webkit-user-select:none;
-moz-user-select:none;
-ms-user-select:none;
user-select:none;cursor:default;
-webkit-animation-name:snowflakes-fall;
-webkit-animation-duration:25s;
-webkit-animation-timing-function:linear;
-webkit-animation-iteration-count:infinite;
-webkit-animation-play-state:running;
animation-name:snowflakes-fall;
animation-duration:20s;
animation-timing-function:linear;
animation-iteration-count:infinite;
animation-play-state:running;
}

.snowflake:nth-of-type(0){left:1%;-webkit-animation-delay:0s,0s;animation-delay:0s,0s}.snowflake:nth-of-type(1){left:10%;-webkit-animation-delay:1s,1s;animation-delay:1s,1s}.snowflake:nth-of-type(2){left:20%;-webkit-animation-delay:6s,.5s;animation-delay:6s,.5s}.snowflake:nth-of-type(3){left:30%;-webkit-animation-delay:4s,2s;animation-delay:4s,2s}.snowflake:nth-of-type(4){left:40%;-webkit-animation-delay:2s,2s;animation-delay:2s,2s}.snowflake:nth-of-type(5){left:50%;-webkit-animation-delay:8s,3s;animation-delay:8s,3s}.snowflake:nth-of-type(6){left:60%;-webkit-animation-delay:6s,2s;animation-delay:6s,2s}.snowflake:nth-of-type(7){left:70%;-webkit-animation-delay:2.5s,1s;animation-delay:2.5s,1s}.snowflake:nth-of-type(8){left:80%;-webkit-animation-delay:1s,0s;animation-delay:1s,0s}.snowflake:nth-of-type(9){left:90%;-webkit-animation-delay:3s,1.5s;animation-delay:3s,1.5s}.snowflake:nth-of-type(10){left:25%;-webkit-animation-delay:2s,0s;animation-delay:2s,0s}.snowflake:nth-of-type(11){left:65%;-webkit-animation-delay:11s,2.5s;animation-delay:11s,2.5s}.snowflake:nth-of-type(12){left:15%;-webkit-animation-delay:10s,1.5s;animation-delay:10s,1.5s}.snowflake:nth-of-type(13){left:35%;-webkit-animation-delay:13s,3s;animation-delay:13s,3s}.snowflake:nth-of-type(14){left:45%;-webkit-animation-delay:14s,1s;animation-delay:14s,1s}.snowflake:nth-of-type(15){left:65%;-webkit-animation-delay:7s,3s;animation-delay:7s,3s}.snowflake:nth-of-type(16){left:70%;-webkit-animation-delay:15s,9s;animation-delay:15s,9s}.snowflake:nth-of-type(17){left:40%;-webkit-animation-delay:12s,7s;animation-delay:12s,7s}

