[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Nova+Flat');

.image-container > .image {
    margin: 1em auto;
    border-radius: 10px;
}

#page-title {
  display: none;
}

.skysermon-nav {
    color: #fff000;
    text-shadow: 1px 1px 2px rgba(0,0,0,.35);
    border-radius: 1px;
    border: double 3px #fff000;
    background-color: #c70030;
    box-shadow: 1.5px 1.5px 3px #636ce9;
    padding: 0 30px;
    margin: 10px 0 15px;
    margin-bottom: 20px;
}
.skysermon-nav a{
    color: #fff000;
}
div.content-panel.standalone.series div.image-container.aligncenter::before {
    content: "普 施 甘 露 雨";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-family:'微软简魏碑', '华文新魏','Nova Flat', cursive;
    font-size: 400%;
    font-weight: 900;
    color: white;
    text-shadow: 0.25rem 0.188rem 0px rgba(0,0,0,0.15), 0.563rem 0.5rem 0px rgba(0,0,0,0.15);
}

div.content-panel.standalone.series div.image-container.aligncenter {
    position: relative;
}

@media only screen and (max-width: 768px) {
    div.content-panel.standalone.series div.image-container.aligncenter::before {
     font-size: 200%;
    }
}
[[/module]]
