[[module CSS]]
#header {
    height: 240px;
    background-image: url('https://scp-wiki.wikidot.com/local--files/scp-7616/Sigil_Main.png');
    background-position: 50% 40%;
    background-repeat: no-repeat;
    background-size: 200px auto;
}
#page-title {
    display: none;
}

.SpiderBitch{
    color: black;
    position: relative;
    margin-left: 20px;
    margin-bottom: 20px;
    padding-left: 20px;
    background-color: #FF3483;
    width: 88%;
    text-align: left;
    font: 400 .9em 'Open Sans', sans-serif;
    border: 1px solid #FF3483;
}

.SpiderBitch:after {
    content: '';
    position: absolute;
    width: 0;
    height: 0;
    border-top: 15px solid #FF3483;
    border-left: 15px solid transparent;
    border-right: 15px solid transparent;
    top: 0;
    left: -15px;
}

.SpiderBitch:before {
    content: '';
    position: absolute;
    width: 0;
    height: 0;
    border-top: 17px solid #FF3483;
    border-left: 16px solid transparent;
    border-right: 16px solid transparent;
    top: -1px;
    left: -17px;
}

.BadBitch{
    color: black;
    position: relative;
    margin-bottom: 20px;
    margin-left: 20px;
    padding-left: 20px;
    background-color: #870F06;
    width: 88%;
    text-align: left;
    font: 400 .9em 'Open Sans', sans-serif;
    border: 1px solid #870F06;
}

.BadBitch:after {
    content: '';
    position: absolute;
    width: 0;
    height: 0;
    border-bottom: 15px solid #870F06;
    border-left: 15px solid transparent;
    border-right: 15px solid transparent;
    bottom: 0;
    right: -15px;
}

.BadBitch:before {
    content: '';
    position: absolute;
    width: 0;
    height: 0;
    border-bottom: 17px solid #870F06;
    border-left: 16px solid transparent;
    border-right: 16px solid transparent;
    bottom: -1px;
    right: -17px;
}
