<script>
        var scansRow=document.getElementsByClassName("scanRow");
        var scansCol=document.getElementsByClassName("scanCol");
        var clickFlag=0;
        var lo;
            function start(){
                if(clickFlag==0){
                    document.getElementById("clickButton").classList.add("hide");
                    // console.log(document.getElementById("clickButton").classList);
                    document.getElementById("red").style.animation="redMove 1s 1 forwards";
                    document.getElementById("yellow").style.animation="yellowMove 1s 1 forwards";
                    document.getElementById("aqua").style.animation="aquaMove 1s 1 forwards";
                    document.getElementById("blue").style.animation="blueMove 1s 1 forwards";
                    document.getElementById("welcome").style.animation="welcome 1s 1 forwards";
                    clearInterval(lo);
                    for(var i=0;i<scansRow.length;i++){
                        scansRow[i].style.animation="scanB 1s 1 forwards";
                        scansCol[i].style.animation="scanA 1s 1 forwards";  
                    }         
                    setTimeout('document.getElementById("clalibration").onmouseout=over;document.getElementById("clalibration").onmouseenter  ="";',700);
                }
            }
            function over(){
                if(clickFlag==0){
                    document.getElementById("clickButton").classList.remove("hide");
                    document.getElementById("red").style.animation="redOver 1s 1 forwards";
                    document.getElementById("yellow").style.animation="yellowOver 1s 1 forwards";
                    document.getElementById("aqua").style.animation="aquaOver 1s 1 forwards";
                    document.getElementById("blue").style.animation="blueOver 1s 1 forwards";
                    document.getElementById("welcome").style.animation="goodbye 1s 1 forwards";
                    var i=0;
                    setTimeout(function(){
                            clearInterval(lo);
                            lo = setInterval(function(){                               
                                scansRow[i].style.animation="scanRow 2.5s infinite";
                                scansCol[i].style.animation="scanCol 2.5s infinite";    
                                // console.log(scansRow[i].style.animation);                                              
                                i++;
                                if(i==scansRow.length){
                                    clearInterval(lo);
                                    i=0;
                                }
                            },250);
                        }
                    ,1000);
                    setTimeout('document.getElementById("clalibration").onmouseout="";document.getElementById("clalibration").onmouseenter  =start;',700);
                }
            }
            function test(){
        clickFlag=1;
        var i=document.body.clientWidth;
        if(i<1000){
                document.getElementById("red").style.animation="redGo 1s 1 forwards";
                document.getElementById("yellow").style.animation="yellowGo 1s 1 forwards";
                document.getElementById("aqua").style.animation="aquaGo 1s 1 forwards";
                document.getElementById("blue").style.animation="blueGo 1s 1 forwards";
