.container {
  background-color: transparent;
  border-radius: 13px;
  padding: 0;
  margin-bottom: 15px;
}

.container::after {
  content: "";
  clear: both;
  display: table;
}

.container img {
  float: left;
  max-width: 80px;
  width: 100%;
  margin-right: 20px;
  border-radius: 50%;
  border: solid 2px black;
  box-shadow: 0px 7px 13px 0px rgba(0,0,0,0.8);
}

.container img.right {
  float: right;
  margin-left: 20px;
  margin-right: 0;
}

.dialogo-npc {
  float: left;
  border: solid 2px #f8b0a4;
  background-color: #f5cac3;
  border-radius: 13px;
  padding: 10px;
  max-width: 73% !important;
  font-family: "Merriweather", serif;
  font-size: 113%;
  box-shadow: inset 0px 7px 0px 0px rgba(255,255,255,0.3),0px 7px 13px 0px rgba(0,0,0,0.8);
  margin-top: 5px;
}

.dialogo-yo {
  float: right;
  border: solid 2px #f35f5c;
  background-color: #f1a7a5;
  border-radius: 13px;
  padding: 10px;
  max-width: 73% !important;
