[[module CSS]]
@import url('https://scpko.wdfiles.com/local--code/component%3Acolstyle/1');

#footer-bar {
  height: 0;
}

#side-bar {
  margin-top: 0;
}

#toc { border: none; background: transparent; }
#toc #toc-action-bar,
#toc .title { display: none; }
#toc #toc-list { display: flex !important; flex-wrap: wrap; }
#toc #toc-list div { margin-left: 2.5em !important; position: relative; line-height: 1.6; }

blockquote a, blockquote a:visited,
div.blockquote a, div.blockquote a:visited,
.yui-content a, .yui-content a:visited,
.content-panel a, .content-panel a:visited {
  color: rgb(var(--dark-accent));
  font-weight: bold;
}

a.newpage {
  color: rgb(var(--bright-accent));
}

#top-bar .open-menu a, .goto-top a {
  border-color: rgb(var(--bright-accent));
  background-color: rgb(var(--white-monochrome));
  color: rgb(var(--bright-accent));
}

#top-bar .open-menu a {
  top: unset;
  bottom: 0.5em;
  pointer-events: auto;
}

#page-content hr {
  margin: 1.5em 0;
  background-color: #999;
}

ul:not(.yui-nav) li {
  position: relative;
  list-style-type: none;
}

.yui-navset {
  position: relative;
}

.yui-nav {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: flex-end;
}

.yui-nav li {
  display: flex;
  flex-grow: 2;
  order: 2;
  margin: 0;
}

#page-content .yui-nav li:last-child {
  order: 1;
}

.yui-nav li a {
  width: 100%;
  text-align: center;
  font-size: 1.4em;
  font-weight: 900;
}

.yui-navset .yui-nav a {
  position: static;
}

.yui-navset .yui-nav li, .yui-navset .yui-navset-top .yui-nav li {
  margin: 0 0.15em 0 0;
}

.yui-navset .yui-nav .selected, .yui-navset .yui-navset-top .yui-nav .selected {
  margin: 0 0.15em -1px 0;
}

.yui-navset .yui-content {
  background-color: rgb(var(--pale-gray-monochrome));
  padding: 1em;
}

.yui-navset .yui-content li {
    line-height: 2;
}
.yui-navset .yui-content li > ul > li {
    line-height: 1.5;
}

#page-content .yui-navset:nth-child(2) li:nth-child(2) a::after,
#page-content .yui-navset:nth-child(2) li:nth-child(3) a::after {
  position: absolute;
  right: 0;
  bottom: 0;
  width: 50px;
  height: 150px;
}
#page-content .yui-navset:nth-child(2) li:nth-child(3) a::after {
  left: 0;
}

#page-content .yui-navset:nth-child(2) li:not(.selected) a::after {
  content: "";
}

blockquote, div.blockquote {
  border-style: solid;
  word-break: keep-all;
}

.blockquote ul {
  display: flex;
  flex-wrap: wrap;
}

.blockquote ul li {
  margin-right: 3em;
}

.blockquote h4 {
  margin-top: 20px;
}

blockquote strong,
div.blockquote strong {
  color: rgb(var(--dark-accent));
}

.adjust {
  position: relative;
  background-color: rgb(var(--pale-gray-monochrome));
  box-shadow: none;
  box-sizing: border-box;
  padding: 20px;
  padding-top: 25px !important;
  height: 60px;
}

.adjust ul {
  font-weight: 600;
  margin-top: -2px;
  margin-bottom: -2px;
}

.adjust li {
  margin: 5px 0 5px -5px;
}

.adjust .collapsible-block {
  margin-top: 6px;
  margin-bottom: 6px;
}

.adjust a.collapsible-block-link {
  position: relative;
  margin-left: 35px;
  text-decoration: underline;
}

.blockquote ul li::before,
.yui-content ul li::before,
.content ul li::before,
.guide ul li::before,
.adjust a.collapsible-block-link::before,
#toc #toc-list div::before {
  content: "▪";
  position: absolute;
  top: 2px;
  left: -15px;
  font-size: 10px
}

.colmod-block > ul > li:before {
  content: none;
}

.adjust blockquote .collapsible-block,
.adjust .blockquote .collapsible-block {
  margin-top: 12px;
  margin-bottom: 12px;
}

.adjust blockquote a.collapsible-block-link,
.adjust .blockquote a.collapsible-block-link {
  margin-left: 0;
}

.adjust blockquote a.collapsible-block-link::before,
.adjust .blockquote a.collapsible-block-link::before {
  content: none;
}

.targeted {
  padding-top: 20px;
}

.targeted h1, .targeted h2 {
  display: block;
  position: absolute;
  width: 100%;
  text-align: center;
  top: 0;
  left: 50%;
  transform: translateX( -50% );
  font-size: 22px;
  font-weight: bold;
  letter-spacing: -1px;
}

.targeted .content {
  display: none;
}

.targeted .content li,
.adjust a.collapsible-block-link {
  word-break: keep-all;
}

.targeted:target .adjust {
  padding-top: 80px !important;
  height: 100%;
}

.targeted:target h1,
.targeted h2,
.targeted .content {
  display: none;
}

.targeted:target h2,
.targeted:target .content {
  display: block;
  top: 20px;
}

.guide {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  background: #f2f2c2;
  border: 1px solid #999;
  padding: 1em 1em 0 1em;
  margin: -1em;
}

.guide li {
  margin: 10px 0;
  line-height: 1.6 !important;
}

@media (max-width: 767px) {
  ul {
    padding-left: 30px;
  }

  .guide > div > ul {
    padding-left: 20px;
  }

  .yui-nav li a {
    font-size: 1.2em;
    line-height: 2;
  }

  .adjust a.collapsible-block-link {
    margin-left: 25px;
  }

  .targeted h1, .targeted h2 {
    font-size: 20px;
  }
}

[[/module]]


