[[include :scp-sandbox-3:turbo-vision]]
[[include :scp-jp-sandbox2:inc:addspecialcss]]
[[include :etinjat:typeface-cn
|VonwaonBitmap-12px=true
]]


[[div style="display:none"]]
[[[uiu-file-1996-005]]]
[[/div]]

[[module CSS]]


#header {
    background: url(http://scpsandboxcn.wikidot.com/local--files/image/Logo-uiu-1996-005-2.png) 0 16px no-repeat;
background-size: 135px;
}
#search-top-box{
display:none;
}

@media (max-width: 1500px) {
#header h1, #header h2 {
       margin-left: 116px;
       font-size:80%;
       line-height: 1;
}
}

/* 修改过后的列表属性 */
.listbox ul li:first-child {list-style-type:disc !important}
.listbox ul li:first-child ul li {list-style-type:circle !important}

.listbox ul {
    width: 100% !important;
}

.CustomTabRJC .yui-navset ul {
    background-color: #ebeaea;
}

.CustomTabRJC .yui-content {
    background-color: #E7E7E7;
    letter-spacing: 2px;
  /* font-family:'等线','幼圆',kaiti;*/
}

#header h2 span::before,
#header h1 a ,
body{
  font-family:'VonwaonBitmap 12px', 'IPix' , '等线','幼圆',kaiti !important;
}

strong, b{
    font-weight: bolder;
}

.CustomTabRJC p {
    line-height: 140%;
}

table.wiki-content-table th {
    background-color: #D5D5D5;
}

/* 弱化对比 
#side-bar .side-block[style="background-color: #fff0f0;"], #side-bar .side-block[style="background-color: #fff0f0;"] .heading p {
    background-color: #848484 !important;
}

#side-bar .side-block[style="background-color: #f0fff6;"], #side-bar .side-block[style="background-color: #f0fff6;"] .heading p {
    background-color: #bcbcbc !important;
}
*/

/* 删去,#action-area 保留蓝屏，改底栏颜色 */
#page-options-container{
    background: #848484;
    border-image: url(http://scp-sandbox-3.wikidot.com/local--files/turbo-vision/border_white.png) 16 round;
    color: white;
}

/* 修改信息 */
.inline-diff ins {
    background-color: #047F15;
}

.inline-diff del {
    background-color: #000;
}

#header h1 ,#header h2 {
    display: block ;
}

#header h1 a {
    display: block ;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
   /* font-family: 'Andale Mono', 'Courier New','等线', '幼圆',kaiti;*/
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "联邦调查局特异事故处";
    color: #fff;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '忠诚，勇敢，正直';
    color: #fff;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
    /* font-family: 'Andale Mono', 'Courier New', '幼圆';*/
    line-height: 1.2;
}

#header h1, #header h2 {
    margin-left: 160px;
}

.page-rate-widget-box .rate-points{
    background-color: #666 !important;
    color: #FFF !important;
    font-weight: bold !important;
    padding: 0 10px !important;
    -moz-border-radius: 4px 0 0 4px !important;
    border-radius: 0 !important;
        border: none !important; 
}    

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    border: none !important;
}

.page-rate-widget-box .cancel a:hover {
    border-radius:0 !important;
}

.page-rate-widget-box a{
    text-decoration: none !important;
    color: #000 !important;
    background-color: #DDD !important;
    padding: 0 5px !important;
}
.page-rate-widget-box a:hover{
    background-color: #EEE !important;
}

.page-rate-widget-box .cancel, .page-rate-widget-box .cancel a{
    -moz-border-radius:  0 !important;
}

.page-rate-widget-box .cancel {
    background-color: transparent !important;
    border: none !important;
}


[[/module]]
