[[include theme:black-highlighter-theme]]
[[include component:pride-highlighter inc-lgbt= --]]]

[[module css]]
@import url("https://kaktuskontainer.wdfiles.com/local--code/component%3Awoedauthor%3Atheme/1");
.attachment {
    width: 18rem !important;
}

iframe.html-block-iframe {
	width: 100%;
}

div.WoedContainer.Writing {
    display: none;
}

div.WoedContainer.Tech {
    overflow: visible;
    margin: 0 0 6rem 0;
}

div.WoedContainer.Tech .skips,
div.WoedContainer.Tech .WholeContainer {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-flow: column wrap;
    -moz-box-orient: vertical;
    -moz-box-direction: normal;
    -ms-flex-flow: column wrap;
    flex-flow: column wrap;
    -webkit-box-align: stretch;
    -webkit-align-items: stretch;
    -moz-box-align: stretch;
    -ms-flex-align: stretch;
    align-items: stretch;
    -webkit-align-self: flex-start;
    -ms-flex-item-align: start;
    align-self: flex-start;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
    -moz-box-flex: 2;
    -ms-flex-positive: 2;
    flex-grow: 2;
    width: -webkit-calc(100% - (var(--w-fontCalc) + 19vh));
    width: -moz-calc(100% - (var(--w-fontCalc) + 19vh));
    width: calc(100% - (var(--w-fontCalc) + 19vh));
    -webkit-flex-basis: 200vw;
    -ms-flex-preferred-size: 200vw;
    flex-basis: 200vw;
}

div.WoedContainer.Tech .skips {
    height: 5rem !important;
}

div.WoedContainer.Tech div.skips > div.labels > div.title {
    line-height: 1;
    text-align: left;
}

div.WoedContainer.Tech .skips,
div.WoedContainer.Tech .WholeContainer {
    height: 100%;
}

div.WoedContainer.Tech > div.name {
    right: 1vh;
}

div.WoedContainer.Tech .discord {
    text-transform: initial;
    text-align: initial;
    letter-spacing: initial;
}

div.scps-block div.warning {
    background-color: rgb(var(--white-monochrome));
    -webkit-border-radius: 0.5rem;
    -moz-border-radius: 0.5rem;
    border-radius: 0.5rem;
    padding: 0.25em;
    margin: 0.25em;
    font-family: var(--body-font);
    font-size: 65%;
}

div.scps-block div.scp-hover-box {
    text-transform: none;
}

div.WoedContainer.Tech .scps-block {
    border: 0rem solid var(--w-color3);
}

div.WoedContainer.Tech div.scps-block > img:nth-of-type(1) {
    margin: 0.25em;
    border: 0.5em solid rgb(252,252,252);
}

.scps-block div.collapsible-block-content {
    margin: 0 1em 1em;
    padding: 0.25em 1em 1em;
    background-color: var(--w-color1);
    -webkit-border-radius: 0.25rem;
    -moz-border-radius: 0.25rem;
    border-radius: 0.25rem;
}

.scps-block div.collapsible-block-unfolded > div.collapsible-block-content > p > a,
.scps-block div.collapsible-block-unfolded > div.collapsible-block-content > a {
    padding: .5vh .5vw;
    margin: .5vh .5vw;
    -webkit-border-radius: .25vw;
    -moz-border-radius: .25vw;
    border-radius: .25vw;
    background: var(--w-color3);
    font-family: 'Exo 2', sans-serif;
    font-size: -webkit-calc(var(--w-fontCalc) * 1.5);
    font-size: -moz-calc(var(--w-fontCalc) * 1.5);
    font-size: calc(var(--w-fontCalc) * 1.5);
    font-weight: 600;
    display: block;
    color: var(--w-color4);
    cursor: pointer;
    text-decoration: none;
    word-break: normal;
    transition-property: -webkit-backdrop-filter, backdrop-filter, box-shadow, background-color;
    -moz-transition-duration: 300ms;
    -o-transition-duration: 300ms;
    transition-duration: 300ms;
    -moz-transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    -webkit-transition-property: -webkit-backdrop-filter, backdrop-filter, box-shadow, background-color;
    -webkit-transition-duration: 300ms;
    -webkit-transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
}

.scps-block div.collapsible-block-unfolded > div.collapsible-block-content > p > a:hover,
.scps-block div.collapsible-block-unfolded > div.collapsible-block-content > a:hover {
    -webkit-box-shadow: 0 0.188rem 0.25rem rgba(0, 0, 0, .8);
    -moz-box-shadow: 0 0.188rem 0.25rem rgba(0, 0, 0, .8);
    box-shadow: 0 0.188rem 0.25rem rgba(0, 0, 0, .8);
    background: var(--w-color5);
    text-decoration: none;
}

div.foldable-list-container {
    position: relative;
    margin: 0.25rem 0;
    border-top: 0.125rem solid var(--w-color4);
    border-bottom: 0.125rem solid var(--w-color4);
}

div.scps-block > div:first-of-type {
    margin-top: 0.5rem;
}

div.scps-block > div:last-of-type {
    margin-bottom: 0.5rem;
}

#page-content > div > div.WholeContainer > div:nth-child(1) > div.scps-block > div:nth-child(6) > div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-content > p:nth-child(2) {
    display: none;
}

div.foldable-list-container div.collapsible-block {
    position: relative;
    z-index: 2;
    background: var(--w-color3);
}

div.preview_img {
    position: absolute;
    padding: 1em;
    background-color: var(--w-color3);
    z-index: 1;
    pointer-events: none;
    opacity: 0;
    width: -webkit-calc(100% - 2rem);
    width: -moz-calc(100% - 2rem);
    width: calc(100% - 2rem);
    max-height: 0%;
    overflow: hidden;
    top: 3.361rem;
    -webkit-border-bottom-right-radius: 0.5rem;
    -moz-border-radius-bottomright: 0.5rem;
    border-bottom-right-radius: 0.5rem;
    -webkit-border-bottom-left-radius: 0.5rem;
    -moz-border-radius-bottomleft: 0.5rem;
    border-bottom-left-radius: 0.5rem;
    border-bottom: 0.125rem solid var(--w-color4);
    -o-transition-property: opacity, max-height;
    -moz-transition-property: opacity, max-height;
    transition-property: opacity, max-height;
    -moz-transition-duration: 300ms;
    -o-transition-duration: 300ms;
    transition-duration: 300ms;
    -moz-transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    -webkit-transition-property: opacity, max-height;
    -webkit-transition-duration: 300ms;
    -webkit-transition-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    left: 1rem;
}

div.collapsible-block:hover + div.preview_img {
    opacity: 1;
    max-height: 2000%;
}

div.preview_img > img {
    -webkit-border-radius: 0.5rem;
    -moz-border-radius: 0.5rem;
    border-radius: 0.5rem;
}

div.WholeContainer > div:nth-of-type(1) {
    z-index: 3;
}

div.WholeContainer > div:nth-of-type(2) {
    z-index: 2;
}

div.WholeContainer > div:nth-of-type(2) {
    z-index: 1;
}

div.scps-block > div:nth-of-type(1) {
    z-index: 12;
}

div.scps-block > div:nth-of-type(2) {
    z-index: 11;
}

div.scps-block > div:nth-of-type(3) {
    z-index: 10;
}

div.scps-block > div:nth-of-type(4) {
    z-index: 9;
}

div.scps-block > div:nth-of-type(5) {
    z-index: 8;
}

div.scps-block > div:nth-of-type(6) {
    z-index: 7;
}

div.scps-block > div:nth-of-type(7) {
    z-index: 6;
}

div.scps-block > div:nth-of-type(8) {
    z-index: 5;
}

div.scps-block > div:nth-of-type(9) {
    z-index: 4;
}

div.scps-block > div:nth-of-type(10) {
    z-index: 3;
}

div.scps-block > div:nth-of-type(11) {
    z-index: 2;
}

div.scps-block > div:nth-of-type(12) {
    z-index: 1;
}

/* Theme-Specific Styling */

.foldable-list-container.BHL,
.foldable-list-container.BHL > *,
.foldable-list-container.BHL a.collapsible-block-link {
    background-color: transparent;
    text-shadow: 0 0 0 rgb(100,3,15), 0 0 0 rgb(100,3,15);
    color: rgb(133, 0, 5);
}

.foldable-list-container.BHL > .collapsible-block {
    overflow: hidden;
}

.foldable-list-container.BHL > .collapsible-block::before {
	content: " ";
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    background: -webkit-gradient(linear,
        left bottom, left top,
        from(rgb(100,46,44)),
        color-stop(90%, rgb(12,12,12)),
        to(rgb(12,12,12)));
    background: -webkit-linear-gradient(bottom,
        rgb(100,46,44) 0%,
        rgb(12,12,12) 90%,
        rgb(12,12,12));
    background: -moz-linear-gradient(bottom,
        rgb(100,46,44) 0%,
        rgb(12,12,12) 90%,
        rgb(12,12,12));
    background: -o-linear-gradient(bottom,
        rgb(100,46,44) 0%,
        rgb(12,12,12) 90%,
        rgb(12,12,12));
    background: linear-gradient(0deg,
        rgb(100,46,44) 0%,
        rgb(12,12,12) 90%,
        rgb(12,12,12));
    -webkit-transform: translateX(-100%);
       -moz-transform: translateX(-100%);
        -ms-transform: translateX(-100%);
         -o-transform: translateX(-100%);
            transform: translateX(-100%);
    opacity: 0;
    -webkit-transition: 
        opacity 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -webkit-transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: 
        opacity 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -webkit-transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition: 
        opacity 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -o-transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -moz-transition: 
        transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1),
        opacity 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -moz-transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: 
        transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1),
        opacity 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: 
        transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1),
        opacity 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -webkit-transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -moz-transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -o-transform 300ms 100ms cubic-bezier(0.4, 0.0, 0.2, 1);
    z-index: -1;
}

.foldable-list-container.BHL > .collapsible-block:hover::before {
	-webkit-transform: translateX(0%);
	   -moz-transform: translateX(0%);
	    -ms-transform: translateX(0%);
	     -o-transform: translateX(0%);
	        transform: translateX(0%);
    opacity: 1;
}

.foldable-list-container.BHL .preview_img {
    background-color:  rgb(100,46,44);
}

.foldable-list-container.BHL .collapsible-block {
    background-color: rgb(244, 244, 244) !important;
}

.foldable-list-container.BHL .collapsible-block-content {
    background: -webkit-gradient(linear, left bottom, left top, color-stop(0, rgb(100, 46, 44)), color-stop(90%, rgb(12, 12, 12)), to(rgb(12, 12, 12)));
    background: -webkit-linear-gradient(bottom, rgb(100, 46, 44) 0, rgb(12, 12, 12) 90%, rgb(12, 12, 12) 100%);
    background: -moz-linear-gradient(bottom, rgb(100, 46, 44) 0, rgb(12, 12, 12) 90%, rgb(12, 12, 12) 100%);
    background: -o-linear-gradient(bottom, rgb(100, 46, 44) 0, rgb(12, 12, 12) 90%, rgb(12, 12, 12) 100%);
    background: linear-gradient(to top, rgb(100, 46, 44) 0, rgb(12, 12, 12) 90%, rgb(12, 12, 12) 100%);
}

.foldable-list-container.BHL div.collapsible-block-unfolded > div.collapsible-block-content > a {
    background-color: rgb(133, 0, 5);
    color: rgb(244, 244, 244);
}

.foldable-list-container.BHL div.collapsible-block-unfolded > div.collapsible-block-content > a:hover {
    -webkit-box-shadow: 0.188rem 0.188rem 0 rgba(198, 0, 7, 1);
    -moz-box-shadow: 0.188rem 0.188rem 0 rgba(198, 0, 7, 1);
    box-shadow: 0.188rem 0.188rem 0 rgba(198, 0, 7, 1);
}

.foldable-list-container.BHL div.collapsible-block-unfolded > div.collapsible-block-content > a:hover {
    -webkit-box-shadow: 0 0.188rem 0.25rem rgba(133, 0, 5, .8);
    -moz-box-shadow: 0 0.188rem 0.25rem rgba(133, 0, 5, .8);
    box-shadow: 0 0.188rem 0.25rem rgba(133, 0, 5, .8);
}

.foldable-list-container.WL,
.foldable-list-container.WL > *,
.foldable-list-container.WL a.collapsible-block-link {
    background: url("https://wanderers-library.wikidot.com/local--files/component:theme/wlheader_bg.png") !important;
    -webkit-background-size: 100% 100%;
    -moz-background-size: 100%;
    -o-background-size: 100%;
    background-size: 100%;
    color: rgb(255, 219, 90);
}

.foldable-list-container.WL .collapsible-block-content {
    background: rgb(30, 55, 30);
}

.foldable-list-container.WL div.collapsible-block-unfolded > div.collapsible-block-content > a {
    background-color: rgb(228, 180, 28);
    color: rgb(30, 55, 30);
}

.foldable-list-container.WL div.collapsible-block-unfolded > div.collapsible-block-content > a:hover {
    -webkit-box-shadow: 0.188rem 0.188rem 0 rgba(255, 219, 90, 1);
    -moz-box-shadow: 0.188rem 0.188rem 0 rgba(255, 219, 90, 1);
    box-shadow: 0.188rem 0.188rem 0 rgba(255, 219, 90, 1);
}

.foldable-list-container.UIU,
.foldable-list-container.UIU > *,
.foldable-list-container.UIU a.collapsible-block-link {
    background: unset !important;
    -webkit-background-size: 100% 100%;
    -moz-background-size: 100%;
    -o-background-size: 100%;
    background-size: 100%;
    color: rgb(230, 234, 24);
    -webkit-transition:
        text-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        color 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -webkit-box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
        text-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        color 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -webkit-box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition:
        text-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        color 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -moz-transition:
        text-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        color 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -moz-box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
        text-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        color 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
        text-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        color 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
        box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -webkit-box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1),
        -moz-box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.foldable-list-container.UIU {
    background: -webkit-linear-gradient(55deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
    background: -moz-linear-gradient(55deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
    background: -o-linear-gradient(55deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
    background: linear-gradient(35deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
}

.foldable-list-container.UIU > .preview_img {
    background: -webkit-linear-gradient(55deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
    background: -moz-linear-gradient(55deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
    background: -o-linear-gradient(55deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
    background: linear-gradient(35deg,
        rgb(15, 11, 12) 0,
        rgb(26, 20, 71) 90%,
        rgb(26, 20, 71)) !important;
}

.foldable-list-container.UIU:hover {
    -webkit-box-shadow: inset 100vw 0 0 0 rgba(239, 221, 27, 1);
       -moz-box-shadow: inset 100vw 0 0 0 rgba(239, 221, 27, 1);
            box-shadow: inset 100vw 0 0 0 rgba(239, 221, 27, 1);
}

.foldable-list-container.UIU:hover a.collapsible-block-link {
    color: rgb(15, 11, 12);
    text-shadow: -0.125rem 0.125rem 0 rgb(227, 69, 38), -0.188rem 0.188em 0 rgb(227, 69, 38);
}

.foldable-list-container.UIU .collapsible-block-content {
    background: rgb(239, 221, 27);
}

.foldable-list-container.UIU div.collapsible-block-unfolded > div.collapsible-block-content > a {
    background-color: rgb(26, 20, 71);
    color: rgb(239, 221, 27);
}

.foldable-list-container.UIU div.collapsible-block-unfolded > div.collapsible-block-content > a:hover {
    -webkit-box-shadow: 0.188rem 0.188rem 0 rgba(15, 11, 12);
       -moz-box-shadow: 0.188rem 0.188rem 0 rgba(15, 11, 12);
            box-shadow: 0.188rem 0.188rem 0 rgba(15, 11, 12);
    background-color: rgb(148, 26, 29);
}

.foldable-list-container.TFT,
.foldable-list-container.TFT > *,
.foldable-list-container.TFT a.collapsible-block-link {
    background: unset !important;
    background-size: 100%;
    color: rgb(252, 252 ,252);
    transition:
        text-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1), color 300ms cubic-bezier(0.4, 0.0, 0.2, 1), box-shadow 1000ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.foldable-list-container.TFT {
	background: 
        linear-gradient(
            rgb(100, 3, 15) 0%, 
            rgb(12, 12, 12) 90%, 
            rgb(12, 12, 12) 100%) !important;
}

.foldable-list-container.TFT::before {
    content: " ";
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    pointer-events: none;
    background: 
        url("https://scp-wiki.wikidot.com/local--files/component:the-foundation-theme/cons_circle.svg"), 
        linear-gradient(
            rgb(100, 3, 15) 0%, 
            rgb(12, 12, 12) 90%, 
            rgb(12, 12, 12) 100%);
    background-size: 200%, 200%;
    background-position: center -10.5rem, center center;
    background-blend-mode: overlay !important;
    z-index: 2;
}

.foldable-list-container.TFT > .preview_img {
    background: rgb(200, 0 ,5) !important;
}

.foldable-list-container.TFT:hover {
    box-shadow: inset 100vw 0 0 0 rgba(200, 0 ,5, 1);
}

.foldable-list-container.TFT a.collapsible-block-link {
    z-index: 9;
}

.foldable-list-container.TFT:hover a.collapsible-block-link {
    color: rgb(252,252,252);
    text-shadow: -0.125rem 0.125rem 0 rgb(227, 69, 38), -0.188rem 0.188em 0 rgb(227, 69, 38);
}

.foldable-list-container.TFT .collapsible-block-content {
    background: rgb(200, 0 ,5);
}

.foldable-list-container.TFT div.collapsible-block-unfolded > div.collapsible-block-content > a {
    background-color: rgb(12,12,12);
    color: rgb(200, 0 ,5);
}

.foldable-list-container.TFT div.collapsible-block-unfolded > div.collapsible-block-content > a:hover {
    color: rgb(252,252,252);
    box-shadow: 0.188rem 0.188rem 0 rgba(12, 12, 12);
    background-color: rgb(148, 26, 29);
}

.foldable-list-container.CHSCP,
.foldable-list-container.CHSCP > div,
.foldable-list-container.CHSCP a.collapsible-block-link {
    background: rgb(195, 7, 7) !important;
    color: rgb(234, 224, 224);
}

.foldable-list-container.CHSCP {
    position: relative;
}

.foldable-list-container.CHSCP::before {
    background-image: url("https://scp-wiki.wdfiles.com/local--files/component%3Ach-scp-theme/subtle_white_mini_waves.png");
    -webkit-background-size: auto auto;
    -moz-background-size: auto;
    -o-background-size: auto;
    background-size: auto;
    background-repeat: repeat;
    background-position: 0 0;
    background-blend-mode: soft-light;
    content: " ";
    z-index: 5;
    height: 100%;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    pointer-events: none;
}

.foldable-list-container.CHSCP .collapsible-block-content {
