content:
[[include theme:black-highlighter-theme]]
[[include component:pride-highlighter inc-lgbt= --]]]

[[module CSS]]
@import url("https://kaktuskontainer.wdfiles.com/local--code/component%3Awoedauthor%3Atheme/1");
div.WoedContainer.Tech {
	display: none;
}

.unicode {
font-family: monospace ! important;
}

#content-wrap {
    font-size: inherit;
}

#page-title {
    pointer-events: none;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;}

.info-container .collapsible-block-link, 
.info-container:hover .collapsible-block-link {
line-height: 1.6em;
}

.code {
    display:none;
}

.page-rate-widget-box {
    overflow: hidden;
    position: relative;
}

iframe.html-block-iframe {
	width: auto;
}

.ko-fi {
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	   -moz-box-pack: center;
	    -ms-flex-pack: center;
	        justify-content: center;
	width: 100%;
	margin: 0.5em 0;
}

#u-scps-block > .list-pages-box {
    position: relative;
    margin: 0.25rem 0;
    border-top: 0.125rem solid var(--w-color4);
    border-bottom: 0.125rem solid var(--w-color4);
}

div#u-scps-block > div:first-of-type {
    margin-top: 0.5rem;
}

@media (max-width: 775px) {
    #page-content > div:nth-child(1) > div {
        margin-top: 0vh !important;
    }
    .page-rate-widget-box {
        width: intrinsic;  
        width: -moz-max-content;    
        width: -webkit-max-content;
        margin: 0 auto 1vh auto !important;
    }
}

.shadow {
-webkit-border-radius: 20px/60px;
-webkit-box-shadow: rgba(0, 0, 0,.30) 0 2px 3px;
  width: 80%;
  margin: auto;
}
.container {
  margin: auto;
  margin-top: -5px;;

  background: #fff;
  border: 1px solid #000;
  -webkit-box-shadow: 1px 1px 2px #fff inset,
              -1px -1px 2px #fff inset;
     -moz-box-shadow: 1px 1px 2px #fff inset,
              -1px -1px 2px #fff inset;
          box-shadow: 1px 1px 2px #fff inset,
              -1px -1px 2px #fff inset;
  -webkit-border-radius: 20px/60px;
     -moz-border-radius: 20px/60px;
          border-radius: 20px/60px;  
}

a img {
    position: relative;
    z-index: 1;
}

#main-content{
    -webkit-align-self: flex-start;
        -ms-flex-item-align: start;
            align-self: flex-start;
    top: 5px;
    left: 0;
    right: 0;
    bottom: 3vh;
}
[[/module]]
