.delivery {
  border-radius: 20px;
  background-color: white;
  padding: 20px;
  margin: 10px;
  filter: drop-shadow(0 0 5px #00000077);
}

.delivery-options {
  display: flex;
  align-items: center;
}

.delivery p {
  margin: 0;
}

.phone {
  width: 30vw;
  height: 60vw;
  border: 1vw solid black;
  border-radius: 20px;
  filter: drop-shadow(0 0 10px var(--shadow-color));
  background-color: #eeeeee;
  display: flex;
  flex-direction: column;
