[[module CSS]]
:root {
  --header-title: "ğ• AI";
  --header-subtitle: "æ‰˜å¸ƒå…°ä¼ä¸šæœ‰é™å…¬å¸é™„å±";
  --main-content-width: 100vw;
  --main-content-side-margin: 0;
  --main-content-top-margin: 0;
  --really-ease-out: 0, 0.99, 0, 1;
  --kinda-ease-out: 0.32, 0.83, 0.31, 0.82;
  --shadow-color: #8c3bf7;
  --orange-highlight: #ff915c;
  --basalt-background-color: 0, 0, 0;
  --logo: url(https://scp-wiki.wdfiles.com/local--files/fragment%3Ay-ai-1/y-ai-1.png);
}

.orange {
  color: #ff915c;
}

.grep-disclaimer {
  font-size: 75%;
  opacity: 0.65;
}

.swap-order {
  flex-direction: row-reverse;
}

#page-content .text-user {
  border-radius: 20px;
  border-bottom-right-radius: 0;
  background-color: #ff915c;
  color: black;
  padding: 20px;
  margin-bottom: 20px;
  max-width: 75%;
  margin-left: auto;
}

#page-content .text-ai {
  border: 1px solid #ffffff88;
  background-color: rgb(40, 40, 40);
  border-radius: 20px;
  border-bottom-left-radius: 0;
  padding: 20px;
  margin-bottom: 20px;
  max-width: 75%;
}

.side-by-side {
  display: flex;
  justify-content: stretch;
}

.side-by-side > * {
  width: 0;
  flex-grow: 1;
}

@media (max-width: 1100px) {
  .side-by-side {
     display: block;
  }

  .side-by-side > * {
    width: unset;
  }
}

@media (max-width: 768px) {
  :root .panel-noborder {
    font-size: 125%;
    margin: 20px;
    padding: 20px;
  }

  :root .panel {
    font-size: 125%;
    margin: 20px;
    padding: 20px;
  }

  :root h2 {
    font-size: 300%;
  }

  #page-content .text-user {
    font-size: 80%;
    padding: 10px;
  }

  #page-content .text-ai {
    font-size: 80%;
    padding: 10px;
  }

  #page-content ul {
    padding-inline-start: 20px;
  }
}

#header {
  position: fixed;
}

#page-title {
  display: none !important;
}

@media (prefers-reduced-motion) {
  .y-ai-hero * {
    animation: none !important;
  }
}

.y-ai-hero {
  height: 100vh;
  background-image: linear-gradient(45deg, rgb(12, 13, 15), rgb(24, 26, 30));
  border-bottom: 1px solid white;
  filter: drop-shadow(0 0 10px var(--shadow-color));
}

.panel {
  margin: 40px;
  padding: 40px;
  background-image: linear-gradient(45deg, rgb(12, 13, 15), rgb(24, 26, 30));
  border: 1px solid white;
  border-radius: 20px;
  filter: drop-shadow(0 0 10px var(--shadow-color));
}

.panel-noborder {
  margin: 40px;
  padding: 40px;
  font-size: 200%;
}

.y-ai-flex {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 50vh;
}

.y-ai-tagline {
  text-align: center;
  font-size: min(5vw, 7.5vh);
  width: min(90%, 40em);
  margin: auto;
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 1.5s both,
    2s cubic-bezier(var(--kinda-ease-out)) fadein-glow 1.5s both;
  --offset: 0 30vw;
  color: white;
}

.y-ai-ai {
  font-size: min(20vw, 30vh);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.84s both,
    2s cubic-bezier(var(--kinda-ease-out)) fadein-glow 1.5s both;
  --offset: 30vw 0;
  vertical-align: center;
}

.y-ai-container {
  width: min(40vw, 60vh);
  height: min(40vw, 60vh);
  animation: 2s cubic-bezier(var(--kinda-ease-out)) fadein-glow 1.5s both;
  position: relative;
  --offset 0 20vw;
  transform: translate(0, -3vh);
}

.y-ai-stem {
  width: 8%;
  height: 40%;
  background-color: white;
  position: absolute;
  bottom: 5%;
  left: 46%;
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein;
  --offset: 0 30vw;
}

.y-ai-right {
  transform-origin: bottom center;
  width: 8%;
  height: 41%;
  background-color: white;
  transform: rotate(45deg);
  position: absolute;
  bottom: 41.5%;
  left: 44.3%;
  clip-path: polygon(0% calc(800% / 41), 100% 0%, 100% 100%, 0% 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.07s both;
  --offset: 30vw -30vw;
}

.y-ai-left-1 {
  transform-origin: bottom center;
  width: 4%;
  height: 40%;
  background-color: white;
  transform: rotate(-45deg);
  position: absolute;
  bottom: 41%;
  left: 45.5%;
  clip-path: polygon(0% 0%, 100% 10%, 100% 100%, 0% 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.14s both;
  --offset: -30vw -30vw;
}

.y-ai-left-2 {
  transform-origin: bottom center;
  width: 4%;
  height: 36%;
  background-color: white;
  transform: rotate(-45deg);
  position: absolute;
  bottom: 43.9%;
  left: 52.5%;
  clip-path: polygon(0% 0%, 100% 11.111111111%, 100% 100%, 0% 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.21s both;
  --offset: -30vw -30vw;
}

.y-ai-left-3 {
  width: 16%;
  height: 4%;
  background-color: white;
  position: absolute;
  bottom: 63.9%;
  left: 20%;
  clip-path: polygon(0% 0%, 75% 0%, 100% 100%, 25% 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.28s both;
  --offset: -30vw 0;
}

.y-ai-left-arrow-1 {
  width: 26%;
  height: 2%;
  background-color: #aaaaaa;
  position: absolute;
  bottom: 70.5%;
  left: 13.3%;
  clip-path: polygon(0% 0%, calc(12 / 13 * 100%) 0%, 100% 100%, 0 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.35s both;
  --offset: -30vw 0;
}

.y-ai-left-arrow-2 {
  width: 2%;
  height: 26%;
  background-color: #aaaaaa;
  position: absolute;
  bottom: 45.5%;
  left: 13.3%;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0 calc(12 / 13 * 100%));
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.42s both;
  --offset: 0 -30vw;
}

.y-ai-right-arrow-1 {
  width: 26%;
  height: 2%;
  background-color: #aaaaaa;
  position: absolute;
  bottom: 70.5%;
  left: 58.3%;
  clip-path: polygon(calc(1 / 13 * 100%) 0%, 100% 0%, 100% 100%, 0 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.49s both;
  --offset: 30vw 0;
}

.y-ai-right-arrow-2 {
  width: 2%;
  height: 26%;
  background-color: #aaaaaa;
  position: absolute;
  bottom: 45.5%;
  left: 82.3%;
  clip-path: polygon(0% 0%, 100% 0%, 100% calc(12 / 13 * 100%), 0 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.56s both;
  --offset: 0 -30vw;
}

.y-ai-bottom-arrow-middle {
  width: 14%;
  height: 2%;
  background-color: #aaaaaa;
  position: absolute;
  bottom: 0%;
  left: 43%;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.63s both;
  --offset: 30vw 0;
}

.y-ai-bottom-arrow-left {
  transform-origin: center right;
  transform: rotate(45deg);
  width: 20%;
  height: 2%;
  background-color: #aaaaaa;
  position: absolute;
  bottom: -0.25%;
  left: 23.6%;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 10% 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.7s both;
  --offset: -30vw -30vw;
}

.y-ai-bottom-arrow-right {
  transform-origin: center left;
  transform: rotate(-45deg);
  width: 20%;
  height: 2%;
  background-color: #aaaaaa;
  position: absolute;
  bottom: -0.25%;
  left: 56.3%;
  clip-path: polygon(0% 0%, 100% 0%, 90% 100%, 0 100%);
  animation: 1s cubic-bezier(var(--really-ease-out)) slidein 0.77s both;
  --offset: 30vw -30vw;
}

@keyframes slidein {
  0% {
    translate: var(--offset);
    opacity: 0;
  }

  100% {
    translate: 0 0;
    opacity: 1;
  }
}

@keyframes fadein-glow {
  0% {
    filter: drop-shadow(0 0 0 transparent);
  }

  100% {
    filter: drop-shadow(0 0 10px var(--shadow-color));
  }
}

h2 {
  text-align: center;
  font-size: 600%;
  margin-top: 100px;
  background-clip: text;
  color: transparent;
  background-image: linear-gradient(45deg, var(--shadow-color), #3b64f7);
  width: fit-content;
  margin-left: auto;
  margin-right: auto;
}

