[[module CSS]]
:root {
  --header-title: "𝕐";
  --header-subtitle: "免于审查的社交媒体";
  --main-content-width: 100vw;
  --main-content-side-margin: 0;
  --main-content-top-margin: 0;
  --really-ease-out: 0, 0.99, 0, 1;
  --kinda-ease-out: 0.32, 0.83, 0.31, 0.82;
  --basalt-background-color: 12, 12, 12;
  --logo: url(https://scp-wiki.wdfiles.com/local--files/fragment%3Ay-ai-1/y-ai-1.png);
  --y-border: #ffffff55;
}


#header {
  position: fixed;
}

#page-title {
  display: none !important;
}

.tweets {
  width: min(600px, 90%);
  margin: auto;
}

.main-header {
  border-top: none;
  border: 1px solid var(--y-border);
}

.main-header h2 {
  text-align: center;
  padding-top: 100px;
  padding-bottom: 100px;
  font-size: 250%;
}

.y-tweet {
  padding: 20px;
  border: 1px solid var(--y-border);
  border-bottom: none;
  margin-bottom: -1px;
}

.with-bottom-border {
  border-bottom: 1px solid var(--y-border);
}

.post-name {
  font-weight: bold;
}

.post-username {
  color: #888888;
}

.grep-fact-check {
  margin: 20px;
  padding: 20px;
  border: 1px solid var(--y-border);
  background-color: #222222;
  border-radius: 20px;
}

.grep-fact-check p:nth-child(1) {
  margin-top: 0;
}

.grep-fact-check p:nth-last-child(1) {
  margin-bottom: 0;
}

.annoying-login {
  display: none;
}

.sign-up-button {
  border-radius: 15px;
  background-color: #333333;
  border: 1px solid var(--y-border);
  width: fit-content;
  padding: 15px;
}

@media (max-width: 1109px) {
  .y-advertisement {
    display: block;
    border-radius: 15px;
    border: 1px solid var(--y-border);
    padding: 15px;
    margin: 15px;
  }
}

@media (min-width: 1100px) {
  .annoying-login {
    display: block;
    border-radius: 15px;
    position: fixed;
    top: 70px;
    right: 10px;
    background-color: #121212;
    border: 1px solid var(--y-border);
    width: calc(50% - 360px);
    padding: 15px;
  }

  .y-advertisement {
    display: block;
    position: fixed;
    top: 70px;
    left: 10px;
    width: calc(50% - 360px);
    border-radius: 15px;
    border: 1px solid var(--y-border);
    padding: 15px;
    animation-name: show-ad;
    animation-duration: 45s;
    animation-iteration-count: infinite;
    visibility: hidden;
  }

  .y-ad-1 {
    animation-delay: 0s;
  }

  .y-ad-2 {
    animation-delay: 15s;
  }

  .y-ad-3 {
    animation-delay: 30s;
  }
}



@keyframes show-ad {
  0% {
    visibility: visible;
  }

  33.332% {
    visibility: visible;
  }

  33.333% {
    visibility: hidden;
  }

  100% {
    visibility: hidden;
  }
}

.pfp {
  width: 2lh;
  height: 2lh;
  float: left;
  border-radius: 50%;
  margin-right: 10px;
}

.verified-factual-by-grep {
  font-style: italic;
  opacity: 0.7;
  display: flex;
  align-items: center;
}

.verified-factual-by-grep p {
  margin: 0;
}

.verified-factual-by-grep::before {
  content: "✔ ";
  font-size: 150%;
  color: #56f241;
  margin-right: 20px;
}

.y-ai-ad-header {
  opacity: 0.7;
  font-style: italic;
}

