  background-color: #333333;
  border: 1px solid var(--y-border);
  width: fit-content;
  padding: 15px;
}

@media (max-width: 1109px) {
  .y-advertisement {
    display: block;
    border-radius: 15px;
    border: 1px solid var(--y-border);
    padding: 15px;
    margin: 15px;
  }
}

@media (min-width: 1100px) {
  .annoying-login {
    display: block;
    border-radius: 15px;
    position: fixed;
    top: 70px;
    right: 10px;
    background-color: #121212;
    border: 1px solid var(--y-border);
    width: calc(50% - 360px);
    padding: 15px;
  }

  .y-advertisement {
    display: block;
    position: fixed;
    top: 70px;
    left: 10px;
    width: calc(50% - 360px);
    border-radius: 15px;
    border: 1px solid var(--y-border);
    padding: 15px;
    animation-name: show-ad;
    animation-duration: 45s;
    animation-iteration-count: infinite;
    visibility: hidden;
  }

  .y-ad-1 {
    animation-delay: 0s;
  }

  .y-ad-2 {
    animation-delay: 15s;
  }

  .y-ad-3 {
    animation-delay: 30s;
  }
}



@keyframes show-ad {
  0% {
    visibility: visible;
  }

  33.332% {
    visibility: visible;
  }

  33.333% {
    visibility: hidden;
  }

  100% {
    visibility: hidden;
  }
}

.pfp {
  width: 2lh;
  height: 2lh;
  float: left;
  border-radius: 50%;
  margin-right: 10px;
}

.verified-factual-by-grep {
