  --main-content-width: 100vw;
  --main-content-side-margin: 0;
  --main-content-top-margin: 0;
  --really-ease-out: 0, 0.99, 0, 1;
  --kinda-ease-out: 0.32, 0.83, 0.31, 0.82;
  --basalt-background-color: 0, 0, 0;
  --logo: url(http://scp-jp-storage.wikidot.com/local--files/file:5384396-393-bpx6/y-ai-1.png);
}

body {
  background-color: linear-gradient(to bottom, #fff4e2 49%, black 51%);
  color: #750001;
}

#header {
  position: fixed;
}

#page-title {
  display: none !important;
}

.screen-sized-panel {
  width: 100%;
  height: 100vh;
}

.hero {
  position: relative;
}

.hero-flex {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  top: 0;
  left: 0;
  position: absolute;
  width: 100vw;
  height: 100vh;
}

.hero h1 {
  font-size: 25vw;
  line-height: 1;
  margin: 0;
  padding: 0;
}


.hero h2 {
  font-size: 3vw;
  max-width: 80%;
}

.screen-sized-panel {
  font-size: 200%;
}

.main-text {
  font-size: 2.75rem;
  padding: 40px;
}

.make-font-smaller {
  font-size: 2rem;
}

.side-by-side {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  height: 100%;
}

.force-center {
  justify-content: center;
  height: 100vh;
}

.side-by-side img {
  max-height: 100vh;
  max-width: 50%;
}

.shrink-img {
  max-width: 50%;
}

.dark-panel {
  color: #ffceb1;
}

.one-character {
  font-size: 30vw;
  line-height: 1;
}

@media (max-width: 768px) {
  .side-by-side {
     flex-direction: column;
     justify-content: center;
  }

  .main-text {
    font-size: 2rem;
  }

  .make-font-smaller {
    font-size: 1.5rem;
  }

  .side-by-side img {
    max-width: 75%;
    margin: -10%;
  }

  .hero h2 {
     font-size: 9vw;
  }

  .one-character {
    font-size: 30vh;
  }
}
[[/module]]




