[[include :scp-wiki-cn:theme:basalt]]

[[module CSS]]
:root {
  --header-title: "𝕐 AI";
  --header-subtitle: "特布兰企业有限公司附属";
  --main-content-width: 100vw;
  --main-content-side-margin: 0;
  --main-content-top-margin: 0;
  --really-ease-out: 0, 0.99, 0, 1;
  --kinda-ease-out: 0.32, 0.83, 0.31, 0.82;
  --basalt-background-color: 0, 0, 0;
  --logo: url(https://scp-wiki.wdfiles.com/local--files/fragment%3Ay-ai-1/y-ai-1-black.png);
  --edit-area-main-background-color: 255, 255, 255;
}

body {
  background-color: #ffffff;
}

#header {
  position: fixed;
}

#page-title {
  display: none !important;
}

.screen-sized-panel {
  width: 100%;
  height: 100vh;
}

.hero {
  position: relative;
}

.hero h1 {
  font-size: 6vw;
  position: absolute;
  top: 43%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  max-width: 1000px;
}


.hero h2 {
  font-size: 3vw;
  position: absolute;
  top: calc(43% + 15vw);
  left: 50%;
  transform: translate(-50%, -50%);
}

.screen-sized-panel {
  font-size: 200%;
}

#page-content .main-text {
  font-size: 2.25rem;
  padding: 40px;
  font-weight: 200;
}

#page-content .standalone-text {
  max-width: 1000px;
  margin: auto;
  font-size: 2.25rem;
  font-weight: 200;
  text-align: center;
  width: min(1000px, 90%);
}

.make-font-smaller {
  font-size: 2rem;
}

.side-by-side {
  display: flex;
  align-items: center;
}

#page-content .main-image {
  width: 40vw;
  max-width: unset;
}

.force-center {
  justify-content: center;
  height: 100vh;
}

#page-content h2 {
  font-size: 800%;
  margin: auto;
  text-align: center;
  margin-top: 40vh;
  margin-bottom: 10vh;
}

@media (max-width: 1100px) {

  .hero h1 {
    font-size: 12vw;
  }

  #page-content h2 {
    font-size: 400%;
  }

  .side-by-side {
     flex-direction: column;
     justify-content: center;
  }

  .isometric-text-container-container {
    width: 75vw;
    margin: auto;
  }

  .isometric-text-container {
    width: 80vw;
    margin: auto;
  }

  #page-content .main-image {
    width: 75vw;
    max-width: unset;
  }

  .swap-order {
    flex-direction: column-reverse;
  }

  #page-content .main-text {
    font-size: 1.75rem;
  }
}

[[/module]]




