[[module CSS]]


.isometric-text-container-container {
  width: min(90%, 800px);
  filter: drop-shadow(-15px 25px 10px #00000044);
}



#page-content .text-user {
  border-radius: 20px;
  border-bottom-right-radius: 0;
  background-color: #ff915c;
  color: white !important;
  padding: 20px;
  margin-bottom: 50px;
  max-width: 75%;
  margin-left: auto;
}

#page-content .text-ai {
  border: 1px solid #ffffff88;
  background-color: rgb(40, 40, 40);
  border-radius: 20px;
  border-bottom-left-radius: 0;
  padding: 20px;
  margin-bottom: 50px;
  max-width: 75%;
  color: white !important;
}



.grepcromancy-ui-container {
  background-image: linear-gradient(45deg, #eeeeee, #dddddd);
  color: #232323;
  padding: 30px;
  padding-bottom: 10px;
  font-size: 75%;
  transform: scale(0.9);
  border-radius: 20px;
}

#page-content .grepcromancy-name {
  font-size: 50%;

  h2 {
    margin-top: 0;
    margin-bottom: 20px;
  }
}

.grepcromancy-slider {
  margin: 30px 10px;
}

.grepcromancy-slider-bg {
  width: 100%;
  height: 4px;
  border-radius; 2px;
  background-color: #444444;
}

.grepcromancy-slider-thumb {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: #000000;
  transform: translateY(-50%) translateY(2px);
}

@media (min-width: 1101px) {

  #page-content .desktop-somewhat-narrow {
    max-width: 50%;
  }

  .grepcromancy-ui-container {
    width: 50%;
    margin: auto;
  }

  .isometric-text-container {
    font-size: 200%;
    transform: rotateX(-45deg) rotateY(45deg) rotateX(90deg);
  }

  .grepcromancy-ui-isometric-container {
    min-width: 55vw;
    margin-left: 35px;
  }

  .right-margin-on-desktop {
    margin-right: 35px;
  }

}



@media (max-width: 1100px) {

  #page-content .grepcromancy-ui-container {
    font-size: 170%;
  }
}

