[[module CSS]]
:root {
  --header-title: "𝕐 AI";
  --header-subtitle: "托布兰企业有限公司所属";
  --main-content-width: 100vw;
  --main-content-side-margin: 0;
  --main-content-top-margin: 0;
  --really-ease-out: 0, 0.99, 0, 1;
  --kinda-ease-out: 0.32, 0.83, 0.31, 0.82;
  --basalt-background-color: 250, 250, 247;
  --logo: url(https://scp-wiki.wdfiles.com/local--files/fragment%3Ay-ai-1/y-ai-1-black.png);
}

#header {
  position: fixed;
}

body {
  background-image: linear-gradient(to bottom, #fafaf7 0%, #fafaf7 95%, black 95.01%, black 100%);
}

@media (max-width: 768px) {
  .y-ai-hero .y-ai-title {
    font-size: 23vw;
    top: 30%;
  }

  .y-ai-hero .y-ai-subtitle {
    top: 50%;
  }
}

#page-title {
  display: none !important;
}

.y-ai-hero {
  width: 100vw;
  height: 100vh;
  position: relative;
  font-family: 'Times New Roman', serif;
}

.y-ai-title {
  position: absolute;
  top: 40%;
  left: 50%;
  translate: -50% -50%;
  font-size: 15vw;
}

.y-ai-subtitle {
  position: absolute;
  top: 65%;
  left: 50%;
  translate: -50% -50%;
  font-size: 200%;
  text-align: center;
}

#page-content {
  font-family: 'Times New Roman', serif;
}

#page-content h2 {
  font-family: 'Times New Roman', serif;
  font-size: 400%;
  margin: 10vh auto;
  text-align: center
}

#page-content p, #page-content li {
  width: min(1000px, 90%);
  margin: auto;
  font-size: 200%;
  line-height: 1.8;
  margin-bottom: 1lh;
}

.short-line {
  width: 30%;
  margin: auto;
  height: 0;
  border-top: 1px solid black;
  font-size: 200%;
  line-height: 1.8;
  margin-bottom: 1lh;
}

#page-content img {
  margin: auto;
  display: block;
}

#page-content * {
  animation-name: misalign;
  animation-fill-mode: both;
  animation-duration: 240s;
  animation-delay: 1s;
}

*:nth-child(4n+1) {
  --rot: 1deg;
}

*:nth-child(4n+2) {
  --rot: -1deg;
}

*:nth-child(4n+3) {
  --rot: -2.4deg;
}

*:nth-child(4n+4) {
  --rot: 1.5deg;
}

p {
  --rot: -2deg;
}

#header {
  --rot: 3deg;
}

a {
  --rot: -4deg;
}

@keyframes misalign {
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(var(--rot));
  }
}

.list-pages-box, .list-pages-item {
  transform: none !important;
}

