[[module CSS]]
#page-title {
   display: none;
}
.footnotes-footer {
   display: none;
}
#breadcrumbs {
   display: none !important;

}

:root:lang(cn) {
   --header-title: "R\0026 C Site-120";
   --header-subtitle: "以火攻火";
   --logo-img: url(https://scp-wiki.wdfiles.com/local--files/secure-facility-dossier-site-120/ontologonew2.png);
   --headerColor: hsl(0, 0%, 20%);
}

.log2 {
   background: white;
   border: 1px solid black;
   padding-left: 1em;
   padding-right: 1em;
   margin: 0.5em 0 0.5em 0;
}

.lognontranslate {
   background: #EBF5FB;
   border: 1px solid black;
   padding-left: 1em;
   padding-right: 1em;
   margin: 0.5em 0 0.5em 0;
}

.metaf {
   background: linear-gradient(180deg, rgba(236,255,254,1) 0%, rgba(189,255,170,1) 100%);
   padding: 10px;
   margin-left: 15%;
   margin-right: 15%;
   outline: solid 3px grey;
   text-align: center;
   transition-property: transform;
   transition-duration: 0.5s;
}

img {
   transition-property: transform;
   transition-duration: 0.5s;
}

.metaf:hover {
   transform: scale(1.1);
}

.metaf:hover img {
   transform: rotate(-15deg);
}

.card {
    background: var(--back-image) center / cover no-repeat;
}
.card > div {
    box-sizing: border-box;
    width: 45%;
    background: linear-gradient(to right, #0008, #0006);
    padding: 0.8rem 0.4rem;
    color: white;
}
.card > div > strong:nth-of-type(1) {
    font-size: 200%;
}
.card > div > strong:nth-of-type(2) {
    font-size: 135%;
    display: inline-block;
}
.card > div > strong:nth-of-type(2)::after {
    content: "";
    display: inline-block;
    width: 100%;
    height: 4px;
    border-radius: 2px;
    background: #E6283C;
    margin: -0.2rem auto 0.6rem
}
.card > div > strong:nth-of-type(3) {
    display: inline-block;
    text-align: justify;
}

@keyframes fade-in {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

.entry {
   background: #fcfcfc;
   margin: 1em 3em;
   padding: 0.75rem;
   border: solid 2px grey;
   text-align: center;
}

@media (max-width: 1250px) {
   .paper,
   .modal,
   .entry,
   .notation {
      margin-left: 0.5em;
      margin-right: 0.5em;
   }
}

.date {
   color: #797979;
   font-size: 120%;
   font-weight: bold;
}

.anchor {
   position: sticky;
   height:0;
   top: 0.5rem;
   z-index: 4;
}

.tocbox {
   margin-top: 0;
   position: absolute;
   top: 0;
   right: max(-18.75rem, -17vw);
   z-index: 4;
   box-sizing: border-box;
   width: max-content;
   pointer-events: none;
}

#page-content .tocbox .toc-u {
   padding: 0.5rem;
   background: hsl(0, 0%, 100%);
   border-right: solid 0.2rem var(--headerColor);
   box-shadow: 0 0 4px rgba(0,0,0,0.3);
   position: relative;
   width: 15rem;
   max-height: calc(98vh - 2rem);
   max-width: calc((30vw - 50% - 6rem)/1.2);
   margin-right: 0; margin-left: auto;
   pointer-events: all;
}

.toc-u #toc::before, .toc-u #toc::after { display: none; }
.toc-u::before, .toc-u::after {
   content: "";
   display: block;
   height: 0.5rem; width: 0.5rem;
   position: absolute;
   right: 0;
   background-color: var(--headerColor);
}

.toc-u::before {
   top: 0;
   transform: translate(calc(0.1rem + 50%),-50%) rotate(45deg);
}

.toc-u::after {
   bottom: 0;
   transform: translate(calc(0.1rem + 50%),50%) rotate(45deg);
}

#page-content .tocbox .toc-u #toc-list {
   overflow-y: auto;
   overflow-x: hidden;
   max-height: 84vh;
   padding: 0 0.5em 1em;
   scrollbar-width: thin;
}
#page-content .tocbox .toc-u #toc-list::-webkit-scrollbar {
   width: 6px;
}

.tocbox table,
.tocbox tbody,
.tocbox tr,
.tocbox td,
.tocbox #toc {
   display: contents;
}

#toc-list > div { position: relative; }
#toc-list > div[style*="1em"]::before {
   content: "•";
   display: inline-block;
   position: absolute;
   left: -0.75em;
   color: var(--accentColor);
}

@media (max-width: 1340px) {
   .anchor {
      filter: none;
      transition: filter 0.2s ease-out;
    }
   .tocbox {
      right: -1rem;
      width: unset;
   }
   .tocbox::before {
      content: "";
      display: block;
      position: absolute;
      background-color: transparent;
      width: 1.5rem; height: 100%;
      top: 0; right: -0.25rem;
      pointer-events: all;
}
   .tocbox::after {
      content: "";
      display: block;
      position: absolute;
      background-color: transparent;
      width: 0; height: 0;
      border: solid 1rem transparent;
      border-left-width: 0.5rem;
      border-right-color: var(--headerColor);
      top: calc(50% - 1rem); right: 0;
      pointer-events: all;
      transition: border-right-width 0.15s ease-out;
   }
   #page-content .tocbox .toc-u {
      width: -moz-fit-content;
      width: fit-content;
      max-width: 60vw;
      box-shadow: none;
      clip-path: inset(-0.5rem -0.5rem -0.5rem calc(100% - 0.65rem));
      transition: clip-path 0.2s ease-out;
      pointer-events: none;
   }
   .anchor:hover { filter: drop-shadow(0px 0px 3px rgba(0,0,0,0.2)); }
   .tocbox:hover {
      pointer-events: all;
   }
   .tocbox:hover::after {
      border-right-width: 0;
   }
   #page-content .tocbox:hover .toc-u {
      pointer-events: all;
      clip-path: inset(-0.5rem -0.5rem -0.5rem -0.35rem);
   }
}

.mobi {
    display: none;
}

@media only screen and (max-width: 675px) {
    .komp {
        display: none;
    }
    .mobi {
        display: inline;
    }
}

