}

@keyframes rotation-9 {
   0% { transform: rotate(calc(0deg / 7)); }
   100% { transform: rotate(calc(-180deg / 7)); }
}

@keyframes bound {
   0% { top: 80px; }
   70% { top: -10px; }
   100% { top: 0px; }
}

@keyframes rotation-o {
   0% { transform: rotate(calc(0deg / 7)); }
   80% { transform: rotate(calc(-1845deg / 7)); }
   100% { transform: rotate(calc(-1800deg / 7)); }
}

@keyframes rotation-a {
   0% { transform: rotate(calc(360deg / 7)); }
   100% { transform: rotate(calc(315deg / 7)); }
}

@keyframes rotation-a-2 {
   0% { transform: rotate(calc(315deg / 7)); }
   100% { transform: rotate(calc(360deg / 7)); }
}

@keyframes anim-scale {
   0% { transform: scale(0.0, 0.0); }
   40% { transform: scale(1.2, 1.2); }
   60% { transform: scale(1, 1); }
   80% { transform: scale(1.1, 1.1); }
   100% { transform: scale(1, 1); }
}

@keyframes back {
   from { background: rgba(240, 248, 255, 1.0); }
   to { background: rgba(240, 248, 255, 0.0); }
}

@keyframes eye-1 {
   from { clip: rect(0px,80px,-5px,0px); }
   to { clip: rect(0px,80px,25px,0px); }
}

@keyframes eye-2 {
   from { clip: rect(0px,80px,80px,0px); }
   to { clip: rect(30px,80px,50px,0px); }
}

@keyframes eye-3 {
   from { clip: rect(85px,80px,80px,0px); }
   to { clip: rect(55px,80px,80px,0px); }
}

@keyframes frown-1 {
   from { top: -5px; }
   to { top: 25px; }
}

@keyframes frown-2 {
   from { bottom: -5px; }
   to { bottom: 25px; }
}

@keyframes opac {
   from { opacity: 0; }
   to { opacity: 1; }
}

@keyframes zoom-out {
   from { transform: scale(1.0); }
   to { transform: scale(0.7); }
}

@keyframes back-2 {
   from { background: rgba(0, 0, 0, 1.0); }
   to { background: rgba(0, 0, 0, 0.3); }
}

