[[include :scp-jp:theme:black-highlighter-theme]]
[[include :scp-jp:theme:crocker-theme]]
[[module css]]
@supports(display:grid) {
  :root {
    --gray-monochrome: 157,144,206;
    --bright-accent: 109,138,239;
    --white-monochrome: 236, 247, 249;
    --pale-gray-monochrome: 218, 240, 243;
    --light-gray-monochrome: 91,194,213;
    --dark-gray-monochrome: 85,149,162;
    --black-monochrome: 39,37,32;
    --medium-accent: 67,69,188;
    --dark-accent: 24,21,129;
    --logo-image: url("http://scp-jp-storage.wikidot.com/local--files/file:6634493-2-tk8t/crocker_logo.png");
    --header-title: "氷我記";
    --header-subtitle: "your ice age";
  }
  #extra-div-1 {
    background-image: url("http://scp-jp-storage.wikidot.com/local--files/file:6634493-1-n8cl/crocker-header-bg.jpg");
  }
}
@media only screen and (max-width: 768px){
  #header h1 a::before {
    font-size: 3.5rem;
  }
  #header {
    background-size: auto 6.375rem;
  }
}
#page-title {
  display: none;
}
#main-content {
  padding: 0px 16px;
}
/*「nice」は「ニセ」と読みます*/
/*horizontal-barはCROCKERテーマから使わせていただいています。そのまま「-----」とするとChromeから分割線が見えなかったためです*/
.nice-title {
  font-size: 30px;
  font-weight: bold;
  font-family: hwt-van-lanen, sans-serif;
  text-align: center;
}
.nice-title p {
  padding: 0px;
  margin: 0px;
}
.horizontal-bar {
    content: " ";
    height: 0.35rem;
    width: 100%;
    margin: 1em auto;
    background: rgb(var(--white-monochrome));
    border-top: 0.125rem dashed rgb(var(--gray-monochrome));
    border-bottom: 0.125rem dashed rgb(var(--gray-monochrome));
    left: 0;
    top: 50%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
.nice-breadcrumbs {
  font-size: 12.6px;
}
[[/module]]
