*/



:root {
    --themeColor: #008484;
    --accentColor: #008484;
}

body {
background-color: #008484;
    background-attachment: fixed;
    font-family: "Microsoft Sans Serif";
-webkit-font-smoothing: none;
    text-rendering: optimizeLegibility;
    image-rendering: pixelated;
}
a{
    color: rgb(0, 0, 255);
}
a:visited{
color: #800080;
}
h1,h2,h3,h4,h5,h6 {
    color: #008484;
    font-family: "Microsoft Sans Serif";
}
div#main-content h1 {
width: 40%;
    margin: auto;
padding: 5px;
    border: #DFDFDF outset 2px;
    background-color: #C0C0C0;
}
div.blockquote {
    border: #DFDFDF outset 2px;
    background-color: #C0C0C0;
        color: black;
        color: #000;
}
div#header {
    background: url(http://scpdsandbox.wikidot.com/local--files/etoisle-authorpage/tl1.png) 2px 15px no-repeat;
}

div#container-wrap {
    background: url(http://www.scp-wiki.net/local--files/is-this-thing-on/glitter1.gif) top left repeat-x;
}

div#header h1 {
    background: url(http://www.scp-wiki.net/local--files/is-this-thing-on/etoile__25743.gif) 50px 30px no-repeat;
}

.scp-image-block{
align: right;
    box-shadow: 4px 4px 0px 0px rgba(0, 0, 0, 0.2);
    border-radius: 0px;
}
div#header h1 a span {
    visibility: hidden;
}
div#header h1 a:before {
    content: " ";
    color: #eee;
    text-shadow: 0;
}

div#header h2 span {
    display: none;
}
div#header h2:after {
    display: none;
}
div#side-bar div.side-block {
    background: #C0C0C0;
    border: #DFDFDF outset 2px;;
    box-shadow: 4px 4px 0px 0px rgba(0, 0, 0, 0.2);
    border-radius: 0px;
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: black;
    border-color: black;
}
div.yui-navset div.yui-content {
    background: #C0C0C0;
border: #DFDFDF outset 2px;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav { /* protect nested tabviews from other orientations */
    border:solid #008484; /* color between tab list and content */
    border-width:0 0 0px;
    Xposition:relative;
    zoom:1;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em{
border-color: transparent;
}
 .yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
     border-color: #008484;
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #C0C0C0;
  border: #DFDFDF outset 2px;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #C0C0C0;
border: #DFDFDF inset 2px;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #008484;
    border: #00aaaa inset 2px;
}
div.open-menu a {
    color: black!important;
    border-color: black!important;
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #C0C0C0!important;
border: #DFDFDF outset 2px !important;
border-radius: 0px;
box-shadow: 4px 4px 0px 0px rgba(0, 0, 0, 0);
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: #DFDFDF outset 2px;!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #000000;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #C0C0C0!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #000000!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: #008484!important;
}
#main-content{
    background: #C0C0C0;
    color: black;
    border: #DFDFDF outset 2px;
box-shadow: 4px 4px 0px 0px rgba(0, 0, 0, 0.2);
font-size: 14px;
}
#side-bar a:visited,
#top-bar a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover {
     color: #800080;
}

#page-title {
   color: #008484;
}

#login-status,
#login-status a {
    color: white;
}

@media (max-width: 767px) {
     #side-bar {
          background-color: #C0C0C0;
     }
}

.side-block .menu-item > .image {
    filter: hue-rotate(250deg);
}

#footer {
    background: transparent;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: #DFDFDF outset 2px;
    background: #C0C0C0;
    box-shadow: none;
    border-radius: 0px;
    color: #000000;
}
span, a, body, h1, h2, h3, h4, h5, h6{
     word-break: unset;
}
[[/module]]
