[[module CSS]]

:root {
--logo-image:url("");
--header-title: 'FYAHGFOX';
 --header-font: "Agency FB", sans-serif;
--header-subtitle: "你无法想象我的能力";
    --rating-module-text-color: 252,252,252;
    --rating-module-button-color: 252,252,252;
    --background-gradient-distance: 0rem;
    --swatch-background: 39, 39, 39;
    --swatch-primary: var(--medium-accent);
    --swatch-primary-darker: var(--medium-accent);
    --swatch-primary-darkest: var(--medium-accent);
    /* Primary Text Colors */
    --swatch-text-dark: var(--white-monochrome);
    --swatch-text-light: var(--white-monochrome);
    --swatch-important-text: var(--bold-accent);
}

.nameone {-webkit-filter: blur(1px);
filter: blur(1px);
}

.nametwo {-webkit-filter: blur(2px);
filter: blur(2px);
}

.namethree {-webkit-filter: blur(3px);
filter: blur(3px);
}

h1 {
    color: white;
}

#header h1 a::before {
    color: white;
}
#page-content > .collapsible-block {
    background-color:#1d1d1d;
    border-top: 0.125rem dashed slategrey;
    border-bottom: 0.125rem dashed slategrey;
}

blockquote, .blockquote {
    color: white;
}

.addendum {
    border:solid 2px #fff;
    background:#292929;
}

.scale, .level-text, .obj-text, .class-text, .itemnum {
   color: white!important;
}

.scp-image-block .scp-image-caption {
    background-color: rgba(0,0,0,.15);
}
#skrollr-body {
    width: 100%;
    height: 7.625rem;
    position: absolute;
    top: 0;
    left: 0;
    background-image:
    linear-gradient(to bottom, rgba(255,255,255,0) 65%, rgba(0,0,0,1) 100%),
    url('https://i.imgur.com/uv18nAU.png');
    background-color: black;
    background-repeat: no-repeat;
    background-size: cover;
}

div.nobody {
    width: 70%;
    margin: 1rem auto;
    text-align: center;
    padding: 0.6rem;
    font-family: "Agency Fb", sans-serif;
    border: 1px solid #CCC;
    border-radius: 5px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;
    color: black;
}

div.nobody a {
    color: rgb(230,40,60);
}
[[/module]]

[[module CSS]]
.authorlink-wrapper {
    --author-top-adjust: 0rem;
    --author-bottom-adjust: 0rem;
}
[[/module]]

