}

/*
サイド
*/

#side-bar {
  min-height: calc(100% + 7em);
  background-color: #b5cde2;
  transform: translateY(-6em);
}

#side-bar .side-block {
  border: none;
  border-radius: 0;
  box-shadow: none;
  background: transparent !important;
  margin-top: 4em;
}
#side-bar .heading {
